$TRANSLATE['56']['47'] = 'Dhe ishin që thonin: “A pasi të vdesim, të bëhemi dhe e eshtra të kalbur, njëmend do të ringjallemi?';
$TRANSLATE['56']['48'] = 'A edhe prindërit tanë të hershëm?';
$TRANSLATE['56']['49'] = 'Thuaj: “Edhe të parët edhe të fundit!”';
$TRANSLATE['56']['50'] = 'Kemi për t’u tubuar në një ditë të caktuar!';
$TRANSLATE['56']['51'] = 'Pastaj ju, o të humbur e gënjeshtarë!';
$TRANSLATE['56']['52'] = 'Keni për të ngrënë prej pemës Zekumë!';
$TRANSLATE['56']['53'] = 'Prej saj keni për t’i mbushur barqet!';
$TRANSLATE['56']['54'] = 'E menjëherë pas do të pini pre ujit t vluar!';
$TRANSLATE['56']['55'] = 'Do të pini si deveja e etshme!”';
$TRANSLATE['56']['56'] = 'Kjo është mirëseardhja e tyre ditën e gjykimit.';
$TRANSLATE['56']['57'] = 'Ne u kemi krijuar juve, e përse nuk pranoni (ringjalljen)?';
$TRANSLATE['56']['58'] = 'A më tregoni për farën që e dredhni,';
$TRANSLATE['56']['59'] = 'A ju e krijoni atë, apo Ne jemi që e krijojmë?';
$TRANSLATE['56']['60'] = 'Ne e kemi caktuar (dhe barazuar) ndërmjet jush vdekjen dhe Ne nuk ka kush që mund të na pengojë.';
$TRANSLATE['56']['61'] = 'Që t’ju zëvendësojë me ë tjerë si ju dhe t’ju krijojmë rishtazi siç nuk dini ju.';
$TRANSLATE['56']['62'] = 'Ju e keni të njohur zanafillën e parë, e përse nuk mendoni.';
$TRANSLATE['56']['63'] = 'A nuk e shihni atë që e mblidhni?';
$TRANSLATE['56']['64'] = 'A ju e bëni të mbijë ajo, apo Ne e bëjmë të mbijë?';
$TRANSLATE['56']['65'] = 'Sikur Ne të dëshironim, do ta bënim atë të thyer (të shkatërruar) e ju do të mbeteshit gjithmonë të habitur (duke thënë).';
$TRANSLATE['56']['66'] = 'S’ka dyshim se ne mbetëm të dëmtuar.';
$TRANSLATE['56']['67'] = 'Po jo, ne mbetëm pa të (të varfëruar).';
$TRANSLATE['56']['68'] = 'A e shihni ujin që po e pini?';
$TRANSLATE['56']['69'] = 'A ju e lëshoni atë prej reve, apo Ne jemi që e lëshojmë?';
$TRANSLATE['56']['70'] = 'Sikur Ne të dëshirojmë, e bëjmë atë të njelmët, përse nuk falënderoni?';
$TRANSLATE['56']['71'] = 'A e keni parë zjarrin që ju e ndizni?';
$TRANSLATE['56']['72'] = 'A ju krijuat drurin e tij, apo Ne jemi krijues?';
$TRANSLATE['56']['73'] = 'Ne e bëmë atë si përkujtim (për zjarrin e Xhehennemit) dhe të nevojshëm për ata në vende të pabanuara (në shkretërirë).';
$TRANSLATE['56']['74'] = 'Pra, ti lartësoje emrin e Zoti tënd të madh.';
$TRANSLATE['56']['75'] = 'Betohem në shuarjen (përëndimin) e yjeve.';
$TRANSLATE['56']['76'] = 'E ai është betim imadh, sikur ta dini!';
$TRANSLATE['56']['77'] = 'Se me të vërtetë ai është Kur’an i famshëm.';
$TRANSLATE['56']['78'] = 'Është në një libër të ruajtur.';
$TRANSLATE['56']['79'] = 'Atë nuk e prek kush, vetëm të pastrit (melaiket).';
$TRANSLATE['56']['80'] = 'Është i zbritur prej Zotit të botëve.';
$TRANSLATE['56']['81'] = 'A, ndaj këtij ligjërimi (Kur’ani) ju jeni që e gënjeni.';
$TRANSLATE['56']['82'] = 'Dhe falënderimi për furnizimin tuaj, ju jeni që e gënjeni.';
$TRANSLATE['56']['83'] = 'Përse, pra kur arrin shpirti në fyt.';
$TRANSLATE['56']['84'] = 'E ju në atë moment shikoni (se ç’po i ngjet).';
$TRANSLATE['56']['85'] = 'E Ne jemi më afër te se ju, por ju nuk shihni.';
$TRANSLATE['56']['86'] = 'Dhe përse, nëse ju nuk jeni përgjegjës (për vepra),';
$TRANSLATE['56']['87'] = '(Përse) Nuk e ktheni atë (shpirtin të mos dalë).';
$TRANSLATE['56']['88'] = 'E nëse ai (i vdekuri) është prej të afërmëve (të Zotit).';
$TRANSLATE['56']['89'] = 'Ai ka (te Zoti) kënaqësi, furnizim të mirë dhe Xhennet të begatshëm.';
$TRANSLATE['56']['90'] = 'Po në qoftë se është nga të djathtët?';
$TRANSLATE['56']['91'] = 'Pra, ty të qoftë se selam prej të djathtëve (i thuhet).';
$TRANSLATE['56']['92'] = 'E në qoftë se është prej gënjeshtarëve të humbur,';
$TRANSLATE['56']['93'] = 'Mirëseardhja e tyre është pritje me ujë të valë.';
$TRANSLATE['56']['94'] = 'Dhe djegie nga zjarri i Xhehennemit.';
$TRANSLATE['56']['95'] = 'E s’ka dyshim se kjo është ajo e vërteta e sigurt.';
$TRANSLATE['56']['96'] = 'Pra ti lartëso Zotin tënd të madhëruar';
$TRANSLATE['57']['1'] = 'All-llahun e lartësoi me adhurim ç’ka në qiej e në tokë dhe Ai është i gjithfuqishmi, i urti.';
$TRANSLATE['57']['2'] = 'I Tij është pushteti në qiej e në tokë, Ai jep jetë dhe Ai jep vdekje dhe Ai ka fuqi për çdo send.';
$TRANSLATE['57']['3'] = 'Ai është i pari që s’ka mbarim, i dukshmi dhe i padukshmi, dhe Ai është më i dijshmi për çdo gjë.';
$TRANSLATE['57']['4'] = 'Ai është që krijoi qiejt dhe tokën vetëm për gjashtë ditë, pastaj qëndroi mbi Arsh. Ai e di çka futet në tokë dhe çka del prej saj, çka zbret nga qielli dhe çka ngritet në të, dhe Ai është me ju kudo që të jeni, All-llahu është përcjellës i asaj që punoni.';
$TRANSLATE['57']['5'] = 'Vetëm i Tij është sundimi i qiejve dhe i tokës dhe vetëm te All-llahu kthehen të gjitha çështjet.';
$TRANSLATE['57']['6'] = 'Ai e ngërthen (e zgjaton) natën në ditë dhe e ngërthen (zgjaton) ditën në natë dhe Ai është që i di të fshehtat në gjoksa.';
$TRANSLATE['57']['7'] = 'Besoni All-llahut dhe të dërguarit të Tij dhe jepni nga ajo që Ai u bëri trashëgues në të, se ata që besuan prej jush dhanë për hir të Zotit, ata kanë shpërblim të madh.';
$TRANSLATE['57']['8'] = 'ç’keni ju që nuk i besoni All-llahut, kue i dërguari i Tij ju thërret t’i besoni Zotit tuaj dhe kur Ai ka marrë besën tuaj, nëse jeni besimtarë (o sot o kurrë).';
$TRANSLATE['57']['9'] = 'Ai robit të vet i shpall argumente të qarta për t’iu nxjerrë juve prej errësirave ne dritë. E, s’ka dyshim se All-llahu është i butë e i mëshirshëm ndaj jush.';
$TRANSLATE['57']['10'] = 'E, ç’keni ju që nuk jepni për rrugën e All-llahut, kur dihet se All-llahut i mbesin trashëgim qiejt dhe toka? Nuk janë të barabartë prej jush ata që dhanë nga pasuria e tyre dhe luftuan para çlirimit, sepse të tillët kanë vlerë të madhe nga ata që dhanë dhe luftuan pas. Por të gjithve All-llahu u premyoi të mirat; All-llahu di çka punoni.';
$TRANSLATE['57']['11'] = 'Kush është ai që i huazon All-llahut një hua të mirë e që Ai t’ia shumëfishojë atë dhe ai ka një shpërbllim të mirë.';
$TRANSLATE['57']['12'] = '(Përkujto) Ditën kur i sheh besimtarët dhe besimtaret që drita e tyre flakëron para tyre dhe në djathtë të tyre. (U thuhet) Myzhde e juaj sot janë Xhennetet nën të cilat rrjedhin lumenj, aty do të jeni përgjithmonë. E ajo është fitore e madhe.';
$TRANSLATE['57']['13'] = 'Ditën kur hipokritët dhe hipokritet atyre që besuan u thonë: “Na pritni (ose na shikoni) të ndriçoheni prej dritës suaj!” U thuhet: “Kthehuni prapa jush (në dynja) e kërkoni ndonjë dritë!” Atëherë vihet ndërmjet tyre njëfarë muri që ka një derë, e brendia e tij është mëshirë (Xhenneti), e ana e jashtme e tij është dënimi (zjarri).';
$TRANSLATE['57']['14'] = '(Hipokritët e mbetur në errësirë) I thërrasin ata (besimtarët): “A nuk kemi qenë edhe ne së bashku me ju!? (Besimtarët përgjigjen): “Po, por ju e shkatërruat veten (me hipokrizi), ju prisnit kob të zi për besimtarët, ju dyshonit çëshjtet e fesë dhe ju mashtruan shpresat e kota deri kur ju erdhi caktimi i All-llahut (vdekja) dhe ashtu, mashtruesi ju mashtroi.';
$TRANSLATE['57']['15'] = 'E sot, prej jush nuk pranohet ndonjë kompensim e as prej atyre që nuk besuan, vendi juaj është zjarri, ai është ndihma e juaj: sa përfundim i keq është ai”.';
$TRANSLATE['57']['16'] = 'A nuk është koha që zemrat të zbuten me këshillat e All-llahut dhe ne atë të vërtetën që zbriti (me Kur’an), e të mos bëhen si ata, të cilëve u është dhënë libri më parë e zhgjati koha dhe zemrat e tyre u shtangën e shumë prej tyre janë jashtë rrugës.';
$TRANSLATE['57']['17'] = 'Dijeni se All-llahu e ngjall tokën pas vdekjes së saj. Ne ua kemi sqaruar argumentet ashtu që të kuptoni.';
$TRANSLATE['57']['18'] = 'S’ka dyshim se ata që japin lëmoshë (sadaka) dhe sinqerisht japin për hirë të All-llahut, atyre u shumëfishohet dhe ata kanë shpërblim të madh.';
$TRANSLATE['57']['19'] = 'Ata që besuan All-llahun dhe të dërguarin e Tij, ata janë të sinqertit dhe dëshmorët te Zoti i tyre, ata kanë shpërblimin e vet, ndërsa ata që e mohuan dhe përgënjeshtruan faktet Tona, ata janë banues të Xhehennemit.';
$TRANSLATE['57']['20'] = 'Ju njerëz dijeni se jeta e kësaj bote nuk është tjetër vetëm se lojë, kalim kohe në argëtim, stoli, krenari mes jush dhe përpjekje në shtimin e pasurisë dhe të fëmijve, e që është shembull i një përpjekje në shtimin e pasurisë dhe të fëmijve, e që është shembull i një shiu prej të cilit bima habit bujqit, e pastaj ajo thahet dhe e sheh atë t verdhë, mandej të thyer e llomitur, e në botën tjetër është dënimi i rëndë, por edhe falje mëkatesh dhe dhurim i kënaqësisë nga All-llahu; pra jeta e kësaj bote nuk është tjetër vetëm se përjetim mashtrues.';
$TRANSLATE['57']['21'] = 'Shpejtoni në atë që e fitoni falje prej Zotit tuaj dhe Xhennetit që gjërsia e qiellit e tokës, i përgaditur për ata që i besuan All-llahut dhe të dërguarve të Tij. Ajo është dhunti e All-llahut ë ia jep kujt të dojë, All-llahu është dhurues i madh.';
$TRANSLATE['57']['22'] = 'Nuk ndodh asnjë fatkeqësi në tokë e as në trupin tuaj, e që të mos jetë në shënime (libër - Lehvi Mahfudh) para se të ngjajë ajo, e kjo për All-llahun është e lehtë.';
$TRANSLATE['57']['23'] = 'Ashtu që të mos dëshproheni tepër për atë që u ka kaluar, e as të mos gëzohi tepër me atë që Ai u ka dhënë, pse All-llahu nuk e do asnjë arrogant që u lavdërohet të tjerëve.';
$TRANSLATE['57']['24'] = 'Të cilët vetë bëjnë koprraci dhe me koprraci i këshillojnë njerzit. E kush largohet prej dhënies, le ta dijë se All-llahu është i begatshëm, i lavdishëm.';
$TRANSLATE['57']['25'] = 'Ne i dërguam të dërguarit Tonë me dokumente të qarta dhe Ne zbritëm me ata librin dhe drejtësinë që t’i përmbahen njerëzit të së drejtës. Ne e kemi zbritur edhe hekurin që në të ka forcë të fortë dhe dobi për njerz, e edhe për t’u ditur te All-llahu se kush ndihmon Atë (rrugën e Tij) dhe të dërguarit e Tij duke i besuar të fshehtës. S’ka dyshim se All-llahu është furnizues, mbizotërues.';
$TRANSLATE['57']['26'] = 'Ne Nuhun dhe Ibrahimin i zgjodhëm të dërguar e pasardhësve të atyre dyve ua dhamë pejgamberllëkun dhe librin, e disa prej tyre po shumë prej tyre mbetën jashtë rrugës.';
$TRANSLATE['57']['27'] = 'Pastaj vazhduam pas tyre me të dërguarit Tanë, e pas tyre dërguam Isain të birin e Merjemes dhe atij ia dhamë Ingjillin, e në zemrat e ithtarëve të tij dhuruam butësi e mëshirë, ndërsa murgësinë ata vetë e shpikën. Ne atë nuk ua bëmë obligim atyre, mirëpo edhe pse kishin për qëllim me të vetëm të arrijnë kënaqësinë e All-llahut, ata nuk iu përmbajtën asaj si duhet përmbajtur, prandaj atyre që besuan, Ne u dhamë shpërblimin e tyre, po shumica prej tyre janë mëkatarë (jobesimtarë).';
$TRANSLATE['57']['28'] = 'O ju që keni besuar, kinie frikë All-llahun dhe vazhdoni me besim ndaj të dërguarit të Tij, se Ai ju jep dyfish nga mëshira e tij, ju dhuron dritë që ecni me të, ju falë mëkatet tuaja All-llahu është mekatfalës, mëshirues.';
$TRANSLATE['57']['29'] = 'Le ta dinë ithtarët e librit se ata nuk kanë asgjë në dorë ng mirësia e All-llahut dhe se të gjithë e mira (pejgamberllëku, shpallja) është në dorë të All-llahut, e Ai ia jep kujt të dojë, All-llahu është dhurues i madh.';
$TRANSLATE['58']['1'] = 'Vërtet, All-llahu dëgjoi fjalët e asaj e cila bisedoi me ty lidhur më burrin e vet, që u ankua te All-llahu; prandaj All-llahu e dëgjon bisedën tuaj, All-llahu dëgjon çdo bisedë dhe sheh çdo punë.';
$TRANSLATE['58']['2'] = 'Ata prej jush që bëjnë “Dhihar” (epërsia ime ndaj teje është haram si epërsia ndaj nënës sime) ndaj grave të veta, në të vërtetë ato nuk janë nënat e tyre, nënat e tyre janë vetëm ato që i kanë lindur ata, ndërsa ata janë janë duke thënë fjalë të irituara dhe gënjeshtra, por All-llahu është që lë shumë pa marrë në përgjegjësi dhe shumë falë.';
$TRANSLATE['58']['3'] = 'Ata që krahasojnë (me nëna) dhe largohen nga gratë e veta, e pastaj zmbrapsen nga ajo që kanë thënë, janë të obliguar që para se të kontaktojnë ndërmjet vete ta lirojnë një rob. Kjo është dispozitë me të cilën porositeni hollësisht me atë që ju e punoni.';
$TRANSLATE['58']['4'] = 'E kush nuk gjen (rob për lirim), atëherë le të agjërojë dy muaj rresht para se të kontaktojnë, e kush nuk mundet, atëherë le t’i ushqejë gjashtëdhjetë të varfër. Kjo (dispozitë ju shërben) për të vërtetën ju se besoni All-llahun dhe të gërguarin e Tij (e jo zakonet injorante). Këto, pra janë përcaktimet e All-llahut. ndërkaq, mohuesit kanë dënim të rëndë.';
$TRANSLATE['58']['5'] = 'Është e vërtetë se ata që i kundërvhen All-llahut dhe të dërguarit të Tij, janë të mposhtur, ashtu siç u mposhtën ata përpara tyre. Mirëpo, Ne u kemi shpallur fakte të qarta, e për mohuesit është një dënim i turpshëm.';
$TRANSLATE['58']['6'] = 'Në ditën e kur All-llahu i ringjall ata të gjithë, i njofton me atë që kanë punuar, pse All-llahu ka mbajtur ato shënime, dhe pse ata i kanë harruar. All-llahu është Ai që çdo send esheh dhe e përcjell.';
$TRANSLATE['58']['7'] = 'A nuk e di ti (o dëgjues) se All-llahu di çka në qiej dhe çka në tokë?! Nuk bëhet bisedë e fshehtë mes tre vetave e të mos jetë Ai i katërti; e as mes pesë vetave e të mos jetë Ai i gjashti, e as mes pak vetave dhe as në shumë vetave, e të mos jetë Ai me ta, kudo që të jenë. Pastaj, në ditën e kijametit i njofton ata me atë që kanë punuar. All-llahu ka përshirë çdo send në dijen e vet.';
$TRANSLATE['58']['8'] = 'A nuk i sheh ata që ishin të ndaluar të bisedojnë fshehtë, se si i kthehen përsëri asaj na e cila ishin të ndaluar dhe pëshpërisin për çka është mëkat, për çka është mizori çka është në kundërshti të të dërguarit. Ata kur vjnë të ti, të përshënesin me atë që All-llahu nuk të përshëndet dhe vetëmevete thonë: “Përse All-llahu nuk na dënon për kët që po e themi?” Por, mjaft është për ta Xhehennemi në të cilin hidhen. Sa vend i shëmtuar është ai.';
$TRANSLATE['58']['9'] = 'O ju që keni besuar, kur bisedoni mes vete fshehurazi, mos bisedoni për çka është mëkat, armiqësi dhe kundërshti ndaj pejgamberit, por bisedoni për punë të ndershme e të matura dhe kinia frikën All-llahut, te i cili do të tuboheni!';
$TRANSLATE['58']['10'] = 'Bisedën e fshehtë e nxit vetëm djalli për t’i shqetësuar ata që besuan, ndonëse ajo nuk mund t’i dëmtojë fare pa dëshirën e All-llahut le t’i mbështetën besimtarët!';
$TRANSLATE['58']['11'] = 'O ju që besuat, kur t’ju thuhet: zgjerohuni (bëni vend) në vend ndeje, ju zgjërohuni, se All-llahu bën zgjërim për ju, e kur t’ju thuhet: ngrituni! Atëherë ju ngrihuni, All-llahu lartëson ata që besuan prej jush, i lartëson në shkallë të lartë ata të cilëve u është dhënë dituri. All-llahu është i njohur mirë me atë që punoni.';
$TRANSLATE['58']['12'] = 'O ju që keni besuar, kur dëshironi të bisedoni me të dërguarin, jepne një sadaka para bisedës suaj. Kjo është për ju më e pastër; e nëse nuk keni çka të jepni, All-llahu është i njohur mirë me atë që punoni.';
$TRANSLATE['58']['13'] = 'A u frikësuat (varfërisë) të jepni lëmoshë në prag të bisedës suaj? E kur ju nuk e bëtë dhe All-llahu ju fali, atëherë pra, falnie namazin, jepnie zeqatin, respekonie All-llahun dhe të dërguarin e Tij, All-llahu është i njohur me punën tuaj.';
$TRANSLATE['58']['14'] = 'A e dini për atë që kanë miqësuar një popull që All-llahu ka shprehur mallkim kundër tyre? Ata nuk janë as prej jush as prej atyre dhe me vetëdije betohen rrejshëm.';
$TRANSLATE['58']['15'] = 'All-llahu për ta ka përgatitur një ndëshkim të ashpër. Vërtet, atë që bëjnë është shumë e keqe.';
$TRANSLATE['58']['16'] = 'Ata betimin e tyre e kanë marrë si mbrojtje dhe kanë penguar prej rrugës së All-llahut, andaj ata i pret një dënim dënim poshtrues.';
$TRANSLATE['58']['17'] = 'Atyre nuk do t’u bëjë kurrfarë dobie pasuria e tyre as fëmijët e tyre para All-llahut. Ata janë banorë të zjarrit, aty janë përgjithmonë.';
$TRANSLATE['58']['18'] = 'Ditën kur All-llahu i ringjall të gjithë, e ata i betohen Atij, sikurse u betoheshin juve, dhe mendojnë (me atë betim) të arrijnë diçka. Pra ta dini, se ata janë me të vërtetë rrenacakët.';
$TRANSLATE['58']['19'] = 'Ata i ka nënshtruar djalli dhe ua ka qitur në harresë porositë e All-llahut. Ata janë shoqëri e djallit, e dinë se ithtarët e djallit janë më të dëshpruarit.';
$TRANSLATE['58']['20'] = 'S’ka dyshim se ata që e kundërshtojnë All-llahun dhe të dërguarin e Tij, të tillët janë ndër më të poshtëruarit.';
$TRANSLATE['58']['21'] = 'All-llahu ka përcaktuar (shkruar në Levhi Mahvudh): “Unë dhe të dërguarit e Mi, pa tjetër do të ngadhnjejmë!” All-llahu është i fortë, ngadhnjyes.';
$TRANSLATE['58']['22'] = 'Nuk gjen popull që e beson All-llahun dhe ditën e gjykimit, e ta dojë atë që e kundërshton All-llahun dhe të dërguarin e Tij, edhe sikur të jenë ata (kundërshtarët) prindërit e tyre, ose fëmijët e tyre, ose vëllezërit e tyre, ose farefisi i tyre. Ata janë që në zemrat e tyre (Ai) ka skalitur besimin dhe ka forcuar me shpirt nga ana e Tij dhe ata do t’i shpjerë në Xhennete, nëpër të cilin rrjedhin lumenj. Aty janë përgjithmonë. All-llahu ua ka pëlqyer punën e tyre dhe ata janë të kënaqur me shpërblimin e Tij. Të tillët janë palë (grup, parti) e All-llahut ta dini pra, se ithtarët e All-llahut janë ata të shpëtuarit.';
$TRANSLATE['59']['1'] = 'All-llahun e madhëron me tesbih (duke i mohuar ato që nuk i takojnë atij) gjithçka ka në tokë, e Ai është i fuqishmi, i urti.';
$TRANSLATE['59']['2'] = 'Ai është që në dëbimin e parë i nxoru prej shtëpive të tyre ata nga ithtarët e librit, të cilët nuk besuan. Ju nuk menduat se fortifikatat e tyre do t’i mbronin prej ndëshkimit të All-llahut, po All-llahu u erdhi atyre nga nuk e kishin menduar dhe në zemrat e tyre hodhi frikën ashu që me duart e veta dhe me duart të besimtarëve të rrëzonin shtëpitë e veta; pra merrni përvojë o ju të zotët e mendjes.';
$TRANSLATE['59']['3'] = 'Sikur All-llahu të mos u kishte caktuar atyre shpërnguljen, ata do t’i ndëshkonte në këtë jetë, kurse në jetën tjetër ata e kanë dënimin e zjarrit.';
$TRANSLATE['59']['4'] = 'Këtë për shkak se e kundërshtuan All-llahun dhe të dërguarin e Tij. Pra, kush e kundërshton All-llahun, s’ka dyshim se All-llahu është ndëshkimfortë.';
$TRANSLATE['59']['5'] = 'Ju nuk këputët asnjë nga hurmat e buta (fidanët) ose atë e keni lënë të ngriur në rrënjët e tye, vetëm se me lejen e All-llahut e për t’i shtypur kundërshtarët.';
$TRANSLATE['59']['6'] = 'E atë (Pasuri) që prej tyre All-llahu ia ktheu të dërguarit të vet, ju për të nuk i keni ngarë as kuajt, as devetë, por All-llahu i bën mbizotëruesit të dërguarit e vet kundër kujt të dojë, All-llahu ka fuqi mbi çdo send.';
$TRANSLATE['59']['7'] = 'Atë që All-llahu nga banorët e vendeve (jobesimtare) ia dha pa luftë të dërguarit të vet, ajo i takon All-llahut, të dërguarit, të afërmve, jetimëve, të varfërve, kurbetçinjve. (Kështu veprohet) Që ajo të mos ndahet ndërrmjet pasanikëve tuaj. Çka t’ju jep i Pejgamberi, atë merrmie e çka t’ju ndalojë, përmbanju dhe kinie frikë All-llahun, se All-llahu është ndëshkues i ashpër.';
$TRANSLATE['59']['8'] = '(Ajo pronë) U takon muhaxhirëve të varfër, të cilët u dëbuan prej shtëpive të tyre dhe prej pasurisë së tyre, duke kërkuar mirasinë dhe kënaqëainë e All-llahut, dhe që ndihmojnë All-llahun dhe të dërguarin e Tij, të tillët janë ata të sinqertit.';
$TRANSLATE['59']['9'] = 'Edhe ata që përgatitën vendin (Medinën) dhe besimin para tyre, i duan ata që shpërnguleshin te ata dhe nuk ndiejnë në gjoksat e tyre ndonjë nevojë (për zili a tjetër) nga ajo që u jepej atyre (muhaxhirëve), madje edhe sikur të kishin vetë nevojë për të, ata u jepnin përparësi atyre para vetvetes. Kush është i ruajtur prej lakmisë së vet, të tillët janë të shpëtuar.';
$TRANSLATE['59']['10'] = 'Edhe ata që kanë ardhur pas tyre e thonë: “Zoti ynë, falna ne dhe vëllezërit tanë që para nesh u pajisën me besim dhe mos lejo në zemrat farë urrejtjeje ndaj atyre që besuan. Zoti ynë Ti je i butë, mëshirues!”';
$TRANSLATE['59']['11'] = 'A nuk e ke ditur se ata që u bënë hipokritë, u thoshin vëllezërve të vet nga ithtarët e librit që nuk kishin besuar: nëse ju dëboheni, edhe ne do të dalim me ju, për çështjen tuaj, ne kurrë nuk do t’i bindemi askujt (t’ju luftojmë ose t’ju nënshtrojmë), e nëse luftoheni prej dikujt, ne gjithqysh do t; u ndihmojmë. All-llahu dëshmon se ata janë rrenacakë!';
$TRANSLATE['59']['12'] = 'Sikur të dëbohen ata (ithtarët e librit), nuk do të dilnin me ta edhe sikur të luftoheshin, nuk do t’u ndihmojnë atyre, po edhe sikur t’u ndihmonin atyre, ata do të iknin prapa, e ata do të mbesin pa ndihmën e tyre.';
$TRANSLATE['59']['13'] = 'Ju (besimtarët) në zemrat e tyre (ta munafikëve) jeni frikë më e fortë se All-llahu (frika nga All-llahu). Kjo për shkak se ata janë popull që nuk kuptojnë.';
$TRANSLATE['59']['14'] = 'Ata të gjithë së bashku nuk kanë guxim t’ju luftojnë, vetëm kur gjenden në ndonjë vend të fortifikuar ose kur janë pas ndonjë muri. Armiqësia ndërmjet tyre është e ashpër. Mund të mendohet për ta se janë të bashkuar, e në reallitet zemrat e tyre janë të përçara, sepse janë njerëz që nuk logjikojnë.';
$TRANSLATE['59']['15'] = 'Si shembull i atyre jo fort moti që përjetuan të keqen e punës së vet, edhe ata kanë një dënim të rëndë.';
$TRANSLATE['59']['16'] = 'Ata (hipokritët) janë shembull si djalli, kur i thotë njeriut: “Refuzo besimin!”, e kur ai të ketë refuzuar, ai (djalli) tërhiqet prej teje; unë i frikohem All-llahut, Zotit të gjithësisë!”';
$TRANSLATE['59']['17'] = 'Dhe ashtu, ata të dy do të përfundojnë në zjarr përgjithmonë. Ai është ndëshkim i zullumqarëve.';
$TRANSLATE['59']['18'] = 'O ju që besuat, frikësojuni All-llahut dhe le të shikojë çdonjëri se çka ka bërë për nesër. Dhe frikësojuni All-llahut! S’ka dyshim se All-llahu është i Njohtuar (Habir) për atë që punoni!';
$TRANSLATE['59']['19'] = 'E mos u bëni si ata që harruan All-llahun, pra (All-llahu bëri që ata) harruan vetveten! Të tillët janë të Fasikët.';
$TRANSLATE['59']['20'] = 'Nuk janë të barabartë banorët e Xhennetit dhe banorët e Xhehennemit. Banorët e Xhennetit janë të suksesshmit (janë ata që ia kanë arritur qëllimit).';
$TRANSLATE['59']['21'] = 'Sikur Ne ta zbritnim këtë Kur’an mbi ndonjë kodër, do të shihje atë të strukur e të çarë prej frikës nga All-llahu. Këta janë shembuj që ua sjellim njerëzve, që ata të mendojnë.';
$TRANSLATE['59']['22'] = 'Ai është All-llahu, nuk ka zot tjetër pos Tij. Dijetari i të fshehtës dhe të dukshmes, Ai është Mëshiruesi, Mëshirëbërësi!';
$TRANSLATE['59']['23'] = 'Ai është All-llahu, pos të Cilit nuk ka zot tjetër, Sunduesi, i Shenjti, i Pastërti (prej të metave që i mvishen), Siguruesi, Mbikëqyrësi, i Plotfuqishmi, Mbizotëruesi, i Madhërishmi. I lartësuar është All-llahu nga ajo që ia shoqërojnë!';
$TRANSLATE['59']['24'] = 'Ai është All-llahu, Krijuesi, Shpikësi, Formësuesi. Të tij janë emrat më të bukur. Atë e lartëson çka ka në qiej e në tokë. Dhe Ai është i Fuqishmi, i Urti!';
$TRANSLATE['60']['1'] = 'O ju që besuat, nëse keni dalë (prej vendlindjes) për hirë të luftës për rrugën Time, duke kërkuar kënaqësinë Time ndaj jush, mos e zini mik armikun Tim dhe armikun tuaj, duke shprehur ndaj tyre dashuri, kur dihet se ata mohuan të vërtetën që u erdhi juve. Ata e dëbojnë të dërguarin dhe ju, sespe i besoni All-llahut, Zotit tuaj, e ju fshehurazi u ofroni miqësi, po Unë më së miri e di atë që e keni fshehur dhe atë që e keni publikuar. Ai që punon ashtu nga pala juaj, ia e ka humbur rrugën e drejtë.';
$TRANSLATE['60']['2'] = 'Nëse ata ia dalin t’ju mindin, ata do të jenë armiq tuaj, do të zgjasin duart e veta dhe gjuhët e veta kundër jush, duke ju përbuzur, sepse ata dëshironin të mos besonit.';
$TRANSLATE['60']['3'] = 'Në ditën e kijametit All-llahu gjykon mes jush, e nuk do t’ju bëjnë dobi as të aërmit tuaj e as fëmijët tuaj. All-llahu sheh atë që punoni.';
$TRANSLATE['60']['4'] = 'Ju e keni shembullin më të mirë te Ibrahimi dhe ata që ishin me të, kur i thanë popullit të vet: “Ne tërhiqemi prej jush dhe prej asaj që adhuroni, pos All-llahut, nuk besojnë tuajën, randaj ndërmjet nesh e jush është e hapët armiqësia e urrejtja derisa ta besoni vetëm All-llahun Një!” (Nuk e kemi shembull) Me përjashtim të fjalës së Ibrahimit thënë babait të vet: “Unë do të kërkoj falje për ty, po unë nuk kam në dorë asgjë për ty te All-llahu!” Zoti ynë, vetëm Ty të jemi mbështetur, vetëm nga Ti jemi kthyer dhe vetëm te Ti është e ardhmja!';
$TRANSLATE['60']['5'] = 'Zoti ynë, mos na bën sprovë në duar të atyre që nuk besuan dhe falna, Zoti ynë, Ti je ngadhënjyesi, i urti.';
$TRANSLATE['60']['6'] = 'Vërtet, për atë që ka shpresë në All-llahun dhe në ditën e gjykimit, ju keni mostër të mirë (te Ibrahimi dhe besimtarët), e kush i bie prapa, le ta dijë se All-llahu është Ai i panevojshmi, i vetvetiu i lavdëruari.';
$TRANSLATE['60']['7'] = 'Pritet që All-llahu të verë miqësi ndërmjet jush dhe atyre që kishit armiqësi, All-llahu është i fuqishëm, All-llahu falë mëkatet, është mëshirues.';
$TRANSLATE['60']['8'] = 'All-llahu nuk ju ndalon të bëni mirë dhe të mbani drejtësi me ata që nuk ju luftuan për shkak të fesë, e as nuk ju dëbuan prej shtëpive tuaja; All-llahu i do ata që mbajnë drejtësinë.';
$TRANSLATE['60']['9'] = 'All-llahu ju ndalon t’u afroheni vetëm atyre që ju luftuan për shkak të fesë, që ju nxorën prej shtëpive tuaja dhe që ndihmuan dëbimin tuaj; ju ndalon t miqësoheni me ta. Kush miqësohet me ta, të tillët janë dëmtues të vetvetës.';
$TRANSLATE['60']['10'] = 'O ju që besuat, kur t’ju vijnë besimtaret e shpërngulura (prej Mekes), provoni besnikërinë e tyre, ndonëse All-llahu di më së miri për besimin e tyre; nëse vërtetoni se ato janë besimtare, atëherë, mos i ktheni ato te jobesimtarët, pse as ato nuk janë të lejuara për ta e as ata nuk janë të lejuar për to, e ju jepnu atyre (jobesimtarëve) atë që kanë shpenzuar për to (në emër të kororëzimit). Ju nuk keni pengesë të martoheni me to, pasi t’ua jepni vlerën e kurorës. Mos i mbani nën kurorë, idhujtaret por kërkoni (prej idhujtarëve mekas) atë që keni shpenzuar për to, dhe ata le të kërkojnë (prej jush besimtarë) atë që kanë shpenzuar. Këto janë dispozita të All-llahut me të cilat Ai gjykon ndërmjet jush; All-llahu është më i dijshmi, më i informuari.';
$TRANSLATE['60']['11'] = 'Në qoftë se u humbet diçka (nga shpenzimi) i ndonjë gruaje tuaj që ikën te idhujtarët, e juve u vjen rasti t’i luftoni dhe të merrni pre të luftës, atëherë atyre që u kanë shkuar gratë, jepnu aq sa kanë shpenzuar; e, kinie frikë All-llahun, të cilin ju e besoni!';
$TRANSLATE['60']['12'] = 'O Pejgamber, kur të vijnë besimtaret që të japin besën se: nuk do t’i shoqërojnë asnjë send All-llahut; se nuk do të vjedhin; se nuk do të bëjnë kurvëri; se nuk do t’i mbysin fëmijët e tyre; se nuk do të gënjejnë me ndonjë shpifje për (fëmijën e huaj që kanë marrë) se është i tyre; dhe se nuk do të të kundërshtojnë në atë që i urdhëron, atëherë, prano zotimin e tyre dhe lute All-llahun t’i falë ato, se All-llahu është mëkatfalës dhe mëshirues.';
$TRANSLATE['60']['13'] = 'O ju që keni besuar, mos u miqësoni me një popull që All-llahu ka shprehur hidhërim kundër tij, popull që ka humbur shpresën ndaj botës së ardhshme, ashtu si nuk kanë shpresë jobesimtarët prej të vdekurve (që do të ngjallen) në këtë botë, ose në ringjallje (në botën tjetër).';
$TRANSLATE['61']['1'] = 'All-llahun e madhëroi çka ka në qiej dhe çka ka në tokë, e Ai është mbizotëruesi, i urti.';
$TRANSLATE['61']['2'] = 'O ju që besuat, pse po e thoni atë që nuk e punoni?';
$TRANSLATE['61']['3'] = 'te All-llahu është shumë e urrejtur ta thoni atë që nuk e punoni!';
$TRANSLATE['61']['4'] = 'All-llahu i do ata që luftojnë në rrugën e Tij të rreshtuar si të jetë ndërtesë e fortifikuar.';
$TRANSLATE['61']['5'] = 'Dhe, (rikujto) kur Musai popullit të vet i tha: “O populli imi, pse më shqetësoni kur e dini se me të vërtetë unë jam i dërguar i All-llahut te ju!” E kur ata u shmangën (nga e vërteta), All-llahui largoi zemrat e tyre, pse All-llahu nuk vë në rrugë të drejtë popullin e prishur.';
$TRANSLATE['61']['6'] = 'Dhe, kur Isai, biri i Merjemes tha: “O beni israilë, unë jam i dërguar i All-llahut te ju, jam vërtetues i Tevratit që ishte para meje dhe jam përgëzues për një të dërguar që do të vijë pas meje, emri i të cilit është Ahmed!” E kur ai u erdhi atyre me argumente të qarta, ata thanë: “Kjo është magji e hapët”.';
$TRANSLATE['61']['7'] = 'E kush është më mizor se ai që për All-llahun shpif gënjeshtër për fenë islame; po All-llahu nuk i ofron udhëzimin për në rrugën e drejtë popullin zullumqarë.';
$TRANSLATE['61']['8'] = 'Ata duan ta shuajnë dritën e All-llahut me gojët e tyre, po All-llahu e plotëson (e përhap) dritën e vet, edhe pse e urrejnë jobesimtarët.';
$TRANSLATE['61']['9'] = 'Ai (All-llahu) është që e dërgoi të dërguarin e vet me udhëzim të qartë e fe të vërtetë për ta bërë mbizotërues mbi të gjitha fetë, edhe pse idhujtarët e urrejnë.';
$TRANSLATE['61']['10'] = 'O ju që keni besuar, a t’ju tregoj për jë tregti të bujshme që ju shpëton prej jë dënimi të dhembshë:';
$TRANSLATE['61']['11'] = 'T’i besoni All-llahut dhe të dërguarit të Tij, të luftoni në rrugën e All-llahut me pasurinë tuaj dhe veten tuaj, e kjo është shumë më e dobishme për ju, nëse jeni që e dini.';
$TRANSLATE['61']['12'] = 'Ai (All-llahu) iu falë mëkatet tuaja, ju shpie në Xhennete nëpër të cilat rrjedhin dhe në pallate të bukura në Xhennetet e amshueshme, e këto janë ai suksesi më i madh.';
$TRANSLATE['61']['13'] = 'Edhe të tjera që ju i dëshironi: ndihmë prej All-llahut dhe fitore e afërt, e pra, përgëzoji besimtarët!';
$TRANSLATE['61']['14'] = 'O ju që besuat, bëhuni ndihmëtarë të All-llahut, si Isai biri i Merjemes havarijunëve (besimtarëve të vet të sinqertë) u pat thënë: “Kush është ndihmëtarë imi për në rrugën e All-llahut?” Havarijunët i thanë: “ne jemi ndihmëtarë të rrugës së All-llahut!” E një grup prej beni israilëve besoi, e një grup refuzoi, e Ne, ata që besuan i përkrahëm kundër armikut të tyre dhe ashtu u bënë ngadhënjyes.';
$TRANSLATE['62']['1'] = 'Ç\'farë ka në qiej dhe ç\'ka në tokë bën tesbih All-llahun, Sunduesin, të Pastërin nga çdo e metë, të Fuqishmin e të Urtin.';
$TRANSLATE['62']['2'] = 'Ai është që arabve të pashkolluar u dërgoi Pejgamberin nga mesi tityre që t’ju lexojë ajetet e Tij, t’i pastrojë ata, t’ua mësojë librin dhe sheriatin, edhe pse më parë ata ishin në një humbje të dukshme.';
$TRANSLATE['62']['3'] = '(U dërgoi) edhe të tjerëve që nuk i arritën ata, Ai është ngadhnjyesi';
$TRANSLATE['62']['4'] = 'Kjo është mirësi e All-llahut, që ia jep atij që do, e All-llahu është Zoti i dhuntisë së përsosur.';
$TRANSLATE['62']['5'] = 'Shembulli i atyre, që janë obliguar me Tevrat, dhe nuk e zbatojnë atë, është shembulli indonjë gomari që bart libra. Shembulli i keq, është shembull i popullit që përgënjeshtroi ajetet e All-llahut, e All-llahu nuk udhëzon në rrugë të drejtë popullin jobesimtarë.';
$TRANSLATE['62']['6'] = 'Thuaj: “O ju që i përmbaheni jehudizmit, nëse e pretendoni se jeni më të afërmit e All-llahut e jo njerëzit e tjerë, atëherë kërkonie vdekjen, po qe se jeni të sinqertë (në atë që thoni)”.';
$TRANSLATE['62']['7'] = 'Po, ata nuk dëshirojnë kurrë atë, për shkak të asaj që e punuan me duart e veta, e All-llahu e di më së miri se kush janë mizorët.';
$TRANSLATE['62']['8'] = 'Thuaj: “s’ka dyshim se vdekja prejsë cilës po ikni, ka për t’ju zën, e mande do të silleni te Ai që e di të padukshmën dhe të dukshëm, dhe atëherë Ai do t’ju njoftojë me atë që keni punuar”.';
$TRANSLATE['62']['9'] = 'O ju që besuat, kur bëhet thirrja për namaz, ditën e zhumasë, ecni shpejt për aty ku përmendet All-llahu (dëgjojeni hutben, falnie namazin), e lini shitblerjen, kjo është më e dobishme për ju nëse jeni që e dini.';
$TRANSLATE['62']['10'] = 'E, kur të kryhet namazi, atëherë shërndanu në tokë dhe kërkoni begatitë e All-llahut, por edhe përmendni shpeshherë All-llahun, ashtu që të gjeni shpëtim.';
$TRANSLATE['62']['11'] = 'Po, kur ata shohin ndonjë tregti, ose ndonjë aheng mësyhen atje, kurse ty të lënë në këmbë. Thuaju: “Ajo që është te All-llahu është shumë më e mira se dëfrimi dhe tregëtia, e All-llahu është furnizuesi më i mirë!”';
$TRANSLATE['63']['1'] = 'Kur të erdhën ty hipokritët, të thanë: “Ne dëshmojmë se vërtet ti je i dërguar i All-llahut!” E, All-llahu e di se ti je i dërguar i Tij, por All-llahu dëshmon se hipokritët janë rrenacakë (kur thonë ashtu).';
$TRANSLATE['63']['2'] = 'Ata betimin e tyre e bëjnë për t’u mbrojtur, andaj ata penguan nga rruga e All-llahut. Veprimi që ata bënë është shumë keq.';
$TRANSLATE['63']['3'] = 'Ngase ata besuan (me gojë) e pastaj mohuan, andaj zemrat e tyre u mbyllën dhe ata nuk kuptojnë.';
$TRANSLATE['63']['4'] = 'E kur t’i shohish ata, trupat e tyre të mahnisin, e kur të flasin, fjalës së tyre i vë veshin. Po ata janë si trungujt e zgavruar e të mbështetur, e çdo zë e mendojnë se është kundër tyre. Ata janë armiq, pra ruaju prej tyre! All-llahu i vraftë, si shmangen nga e vërteta!';
$TRANSLATE['63']['5'] = 'E kur u thuhet: “Ejani te i dërguari i All-llahut që ai të kërkojë prej Tij falje për ju”, ata tundin kokat e tyre dhe i sheh se si ta kthejnë shpinën duke e mbajtur veten të madh.';
$TRANSLATE['63']['6'] = 'Sa u përket atyre, është krejt njësoj: si kërkove për ta falje, si nuk kërkove për ta falje (nga Zoti), sepse All-llahu kurrsesi nuk do t’i falë dhe është e sigurt se All-llahu nuk ia ofron udhëzimin popullit arrogant.';
$TRANSLATE['63']['7'] = 'Ata janë që u thonë (vendasve të Medinës): “Mos u jepni atyre që janë me të dërguarin e All-llahut (muhaxhirëve), ashtu që të shpërndahen!. Po, te All-llahu janë pasuritë (depot) e iqejve e të tokës, por hipokritët nuk janë duke kuptuar”.';
$TRANSLATE['63']['8'] = 'Ata thonë: “Nëse do të kthehemi (prej gjuhës me beni Mustalikët) në Medine, ai më i forti pa tjetër prej aty ka për ta përzënë atë më të dobëtin!” (ashtu thanë) Ndërsa e tërë krenaria i takon All-llahut, të dërguarit të Tij dhe besimtarëve, por hipokritët këtë nuk e dinë.';
$TRANSLATE['63']['9'] = 'O ju që besuat, as pasuria juaj e as fëmijët tuaj të mos u shmangin prej adhurimit të All-llahut, e kush bën ashtu të tillët janë mu ata të humburit.';
$TRANSLATE['63']['10'] = 'Dhe jepni nga ajo që Ne u kemi dhënë juve, para se ndonjërit prej jush t’i vijë vdekja, e atëherë të thotë: “O Zoti im, përse nuk më shtyve edhe pak afatin (e vdekjes), që të jepja lëmoshë e të bëhesha prej të mira!”';
$TRANSLATE['63']['11'] = 'Po, All-llahu kurrsesi askë nuk e shtyn për më vonë, kur atij t’i vijë afati i vet. All-llahu hollësisht është i njohur me atë që ju punoni.';
$TRANSLATE['64']['1'] = 'Vetëm All-llahut i shprehin madhërinë e lartësimin çka ka në qiej dhe çka në tokë, dhe se vetëm e Tij është i tërë pushteti dhe vetëm Atij i takon falënderimi, e Ai është i gjithfuqishëm për çdo send.';
$TRANSLATE['64']['2'] = 'Ai është që u krijoi juve, e nga ju ka mohues dhe ka besimtarë. All-llahu e sheh shumë mirë atë që e punoni.';
$TRANSLATE['64']['3'] = 'Ai krijoi qiejt dhe tokën me urtësi të përsosur dhe Ai juve u dha formën, e formën tuaj e bëri të bukur dhe vetëm te Ai është e ardhmja.';
$TRANSLATE['64']['4'] = 'Ai e di ç’ka në qiej e në tokë, e di atë që e fshihni dhe atë që e shfaqni haptazi, sepse All-llahu di edhe atë që mbajnë në zemrat.';
$TRANSLATE['64']['5'] = 'A nuk ju erdhi lajmi i atyre që ishin më parë e nuk besuan, ndaj edhe përjetuan barrën e rëndë të punës së vet dhe ata kanë një dënim të idhët.';
$TRANSLATE['64']['6'] = 'Atë (dënim e marrin) për shkak se atyre u vinin të dërguarit e vet me argumente të qarta, e ata thoshin: “A njeriu i thjeshtë do të na udhëzojë neve?” Dhe ashtu nuk besuan dhe u zmbrapsën, po All-llahu nuk ka nevojë për ta; All-llahu është begatshëm, e i falënderuar nga vetvetiu.';
$TRANSLATE['64']['7'] = 'Ata që nuk besuan, menduan se kurrsesi nuk do të ringjallen. Thuaj: “Po, pasha Zotin tim, pa tjetër do të ringjalleni dhe do të njiheni me atë që keni punuar, e ajo për All-llahun është lehtë!”';
$TRANSLATE['64']['8'] = 'Atëherë pra, pranonie All-llahun, të dërguarin e Tij dhe dritën që e zbritëm Ne. All-llahu është i njohur për atë që veproni.';
$TRANSLATE['64']['9'] = 'Ditën e kijametit, kur Ai u tubon juve, ajo është ditë e mashtrimit (ditën e kijametit duket se kush është mashtruar). kush beson All-llahun dhe bën vepra të mira. Ai ia shlyen të këqijat e tij dhe atë e fut në Xhennete, nëpër të cilat rrjedhin lumenj; aty do të jenë përgjthmonë, e ai është shpëtimi i madh.';
$TRANSLATE['64']['10'] = 'E ata që nuk besuan dhe përgënjeshtruan argumentet Tona, të tillët janë banorët e zjarrit; aty do të jenë përgjithmonë, e ai është përfundim i keq.';
$TRANSLATE['64']['11'] = 'çfarëdo e keqe që ndodh (godet), nuk mund të jetë ndryshe, vetëm sipas caktimit të All-llahut, e kush i beson All-llahut, Ai ia udhëzon zemrën e tij; All-llahu është i gjithdijshëm për çdo send.';
$TRANSLATE['64']['12'] = 'Pra, respektonie All-llahun dhe respektonie të dërguarin, po nëse nuk e respektoni, atëherë detyrë e të dërguarit tonë është vetëm kumtimi i qartë.';
$TRANSLATE['64']['13'] = 'All-llahu është Ai që pos Tij nuk ka zot tjetër, prandaj vetëm All-llahut le t’i mbështeten besimtarët.';
$TRANSLATE['64']['14'] = 'O ju që besuat, vërtet, disa nga bashkëshortet tuaja dhe nga fëmojët tuaj janë armiq tuaj, andaj kini kujdes ndaj tyre, e nëse nuk merrni masa, nuk ua vini veshin dhe i falni, ta dini se edhe All-llahu është mëkatfalës, mëshirues.';
$TRANSLATE['64']['15'] = 'S’ka dyshim se pasuria juaj dhe fëmijët tuaj janë sprovë, ndërsa ajo që është te All-llahu, është shpërblim i madh.';
$TRANSLATE['64']['16'] = 'Andaj, sa të keni mundësi ruanu prej dënimit të All-llahut, dëgjoni, respektoni dhe jepni për të mirën tuaj, e kush i ruhet lakmisë së vetvetes, të tillët janë të shpëtuarit.';
$TRANSLATE['64']['17'] = 'Nëse All-llahut i huazoni diçka të mirë, Ai juve ua shumëfishon atë dhe ju falë; All-llahu është shumë mirënjohës, i butë.';
$TRANSLATE['64']['18'] = 'Është i gjithdijshmi i të fshehtës e i të dukshmes, është ngadhënjyesi, i urti.';
$TRANSLATE['65']['1'] = 'O ti pejgamber, kur t’i lëshoni gratë, lëshoni ato në vigjilje të fillimit të pritjes së tyre (në kohën e pastërtisë së tyre) dhe kohën e pritjes mbanie mend mirë e kini frikë All-llahut, Zotit tuaj, dhe mos i nxirrni ato prej shtëpive të tyre, e edhe ato të mos dalin ndryshe vetëm nëse bëjnë ndonjë vepër të keqe të vërtetuar. Këto janë dispozitat e All-llahut, e kush del jashtë dispozitave të All-llahut, ai e ka dëmtuar vetveten. Ti nuk e di, All-llahu pas asaj mund të të jap diçka rishtazi.';
$TRANSLATE['65']['2'] = 'E kur t’i afrohen ato mbarimit ta afatit të tyre, atëherë ose mbani (i ktheni në jetën bashkëshortore) si duhet, ose ndanu prej tyre si është e udhës, e këtë do ta dëshmojnë dy dëshmitarë të drejtë nga mesi juaj dhe dëshmitë zbatojeni për hirë të All-llahut. Kështu këshillohet ai që i beson All-llahut dhe ditës së fundit, e kush iu përmbahet dispozitave të All-llahut, atij Ai i hap rrugë,';
$TRANSLATE['65']['3'] = 'dhe e furnizon atë prej nga nuk kujton fare. Kështu i mbështetet All-llahut, Ai i mjafton atij, All-llahu realizon dëshirën e vet dhe All-llahu çdo gjëje ia ka caktuar kohën (afatin).';
$TRANSLATE['65']['4'] = 'E ato nga gratë tuaja të cilave u është ndërprerë cikli mujor (menstruacioni) nëse nuk e keni ditur, koha e pritjes së tyre është tre muaj, e ashtu edhe ato që ende nuk kanë pasur menstruacion. Ndërkaq, për shtatzënat afati i pritjes së tyre është derisa të lindin. E kush i frikësohet All-llahut, Ai atij ia lehtëson punën.';
$TRANSLATE['65']['5'] = 'Ky është vendim i All-llahut që ua zbriti juve, e kush e ka frikë All-llahun, Ai atij ia shlyen të këqijat dhe ia shton shpërblimin.';
$TRANSLATE['65']['6'] = 'Ato (gratë e lëshuara), sipas mundësisë suaj i vendosni në ndonjë vend ku banoni ju, e mos i ngushtoni ato për t’i detyruar (të dalin). Në qoftë se ato janë shtatzëna, atëherë furnizoni ato derisa ta heqin barrën e tyre, e nëse ato u japin gji fëmijëve tuaj, atëherë jepnu atyre shpërblimin që u takon dhe kini mirëkuptim në mes jush ashtu si duhet, e në qoftë se hasni në vështirësi ndër vete, atëherë le të kërkojnë për të gjidhënëse tjetër.';
$TRANSLATE['65']['7'] = 'Ai që është i pasur, le të shpenzojë sipas mundësisë së vet, e ai që është ngusht nga pasuria, le të japë nga ajo që i ka dhënë All-llahu, e All-llahu nuk ngarkon askë, vetëm aq sa i ka dhënë; mirëpo pas vështirësisë All-llahu sjell çlirim (begati).';
$TRANSLATE['65']['8'] = 'Sa shumë banorë të fshatrave, që ishin kryeneçë ndaj urdhërit të Zotit të vet dhe ndaj të dërguarëve të Tij, Ne i kemi shpërblyer me një llogari të ashpër, i kemi ndëshkuar me një dënim të papërshkrueshëm.';
$TRANSLATE['65']['9'] = 'She ata e përjetuan barrën e rëndë të punës së vet, kështu që fundi i çështjes së tyre ishte dështimi.';
$TRANSLATE['65']['10'] = 'All-llahu u ka përgatitur dënim të rëndë, atyre pra, kinie frikë All-llahun o ju të zotët e mendjes. E ju që keni besuar, All-llahu u zbriti juve shpalljen (Kur’anin).';
$TRANSLATE['65']['11'] = 'Të dërguarit që ua lexon ajetet e qarta të All-llahut për t’i nxjerrë ata që besuan dhe bënë cepra të mira prej errësirave në dritë. Kush i beson All-llahut dhe bën vepra të mira, Ai e shpie atë në Xhennete nëpër të cilat rrjedhin lumenj; aty do të jenë përgjthmonë e kurrë pakëputur. All-llahu i ka dhënë atij furnizim të mirë...';
$TRANSLATE['65']['12'] = 'All-llahu është Ai që krijoi shtatë palë qiej dhe prej tokës aq sa ata; në to zbatohet vendimi i Tij për ta ditur ju se All-llahu ka fuqi për çdo send dhe që me të vërtetë All-llahu ka përfshirë çdo send me dijen e vet.';
$TRANSLATE['66']['1'] = 'O ti pejgamber (o ti, që është sjellë kumtesa nga qielli), pse ndalon vetes atë që All-llahu ta lejoni? A kërkon me të pajtim e grave tuaja? All-llahu është mëkatfalës, mëshirues.';
$TRANSLATE['66']['2'] = 'All-llahu u ka përcaktuar juve zgjidhjen e betimeve tuaj; All-llahu është kujdestari juaj; Ai është i gjithdjshmi, i urti.';
$TRANSLATE['66']['3'] = '(Përkujto) kur Pejgamberi ndonjerës prej grave të veta i kumtoi fshehurazi një lajm, e kur ajo tregoi atë (lajm), e All-llahu (Pejgamberit) i zbuloi atij për të (kallzimin e lajmit nga ajo grua), ai ia bëri me (asaj) me dije një pjesë të trgimit, kurse për pjesën tjetër heshti. E kur ai (pejgamberi) e njohtoi atë (gruan) me atë (që kishte treguar), ajo tha: “E kush të tregoi ty këtë?” Ai tha: “Më tregoi i gjithdijshmi, njohësi i të fshehtave!”';
$TRANSLATE['66']['4'] = 'Nëse ju të dyja pendoheni te All-llahu (është më mirë për ju), sepse se zemrat tuaja tanimë kanë rrëshqitur, e nëse bashkveproni kundër tij, ta dini se All-llahu është Ai mbrojtës i tij si dhe Xhibrili dhe besimtarët e ndershëm; e kur është kështu, edhe engjëjt i dalin në ndihmë.';
$TRANSLATE['66']['5'] = 'Nëse ai u ka lëshuar juve, është detyrë e Zotit të tij t’i bëjë zëvendsim me gra edhe më të mira se ju; myslimane, besmtarë, respektuese, penduese, adhuruese, emigruese që kanë qenë të martuara ose virgjiresha.';
$TRANSLATE['66']['6'] = 'O ju që besuat, ruane veten dhe familjen tuaj prej zjarrit, lënda djegëse e të cilit janë njerëzit dhe gurët. Atë (zjarrin) e mbikëqyrin engjëjt të rreptë e të ashpër që nuk e kndërshtojnë All-llahun për asgjë që Ai i urdhëron dhe punojnë atë që janë të urdhëruara.';
$TRANSLATE['66']['7'] = 'O ju që mohuat, sot mos u arsyetoni; ju shpërbleni veten për atë që keni vepruar.';
$TRANSLATE['66']['8'] = 'O ju që keni besuar, pendohuni te All-llahu me një pendim të sinqertë në mënyrë që Zoti juaj t’i largojë prej jush të këqijat, t’u shpie në Xhennete, nën të cilat rrjedhin lumenj ditën kur All-llahu nuk e turpëron Pejgamberin e së bashku me të as atta që kanë besuar. Drita e tyre ndriçon para tyre dhe në të djathtë të të tyre, e ata thonë: “Zoti ynë, vazhdona dritën tonë, falna neve. Vërtet, Ti je i plotfuqishëm për çdo send”.';
$TRANSLATE['66']['9'] = 'O ti pejgamber, luftonju jobesimtarët e hipokritët dhe qëndro ashëpër kundër tyre; vendi i tyre është Xhehennemi që është një vend i shëmtuar!';
$TRANSLATE['66']['10'] = 'Atyre që mohuan, All-llahu u sjell një shembull gruan e Nuhut dhe gruan e Lutit. Ato të dyja ishin në kurorë të dy robërve të mirë nga robërit tanë, por ato të dyja i tradhtuan (në fe) ata të dy dhe këta të dy nuk mund t’i mbrojnë ato fare te All-llahu, e atyre dyjave u thuhet: “Hyni në zjarr së bashku me ata që hyjnë!”';
$TRANSLATE['66']['11'] = 'E atyre që besuan, All-llahun u solli shembull gruan e faraonit kur ajo tha: “Zoti im, më bën një vend pranë mëshirës sate në Xhennet dhe më shpëto prej faraonit e brutalitetit të tij dhe më shpëto prej popullit mizor!”';
$TRANSLATE['66']['12'] = 'Edhe Merjemen të bijën e Imranit, që e ruajti nderin e vet, e Ne prej anës sonë i frymëzuam një shpirt e ajo i besoi fjalët e Zotit të saj dhe librat e Tij dhe ishte e devotshme.';
$TRANSLATE['67']['1'] = 'I madhëruar është Ai, që në dorën e fuqisë së Tij është i tërë sundimi dhe Ai ka fuqi mbi çdo send.';
$TRANSLATE['67']['2'] = 'Ai është që krijoi vdekjen dhe jetën, për t’ju sprovuar se cili prej jush është më vepërmirë. Ai është ngadhnjyesi, mëkatëfalësi.';
$TRANSLATE['67']['3'] = 'Ai është që krijoi shtatë qiej palë mbi palë. Në krijimin e Mëshiruesit nuk mund të shohësh ndonjë kontrast, andaj, drejto shikimin se a sheh ndonjë çarje?';
$TRANSLATE['67']['4'] = 'Mandej, herë pas here drejto shikimin, e shikimi do të kthehet të ti i përulur dhe i molisur.';
$TRANSLATE['67']['5'] = 'Ne, qiellin më të afërt e kemi zbukuruar me kandila (yje ndriquese) dhe ata ikemi bërë gjuajtës kundër djajve, të cilëve u kemi përgatitur dënim me zjarr shumë të madh.';
$TRANSLATE['67']['6'] = 'Dënimi në Xhehennem është edhe për ata që nuk besuan Zotit të tyre; sa vend i keq është ai!';
$TRANSLATE['67']['7'] = 'Kur të hidhen në të, ata do t’ia dëgjojnë atij ushtimën e ai vlon.';
$TRANSLATE['67']['8'] = 'Nga zemërimi, ai gati copëtohet. Sa herë që hidhet në të ndonjë turmë, roja i pyet: “A nuk ju pat ardhur juve ndonjë pejgamber (qortues)?”';
$TRANSLATE['67']['9'] = 'Ata thonë: “Po, na erdhi pejgamberi, por ne e përgënjeshtruam dhe ne u thamë: “All-llahu nuk shpalli asgjë, e ju nukjeni tjetër vetëm se në një humbje të nadhe!”';
$TRANSLATE['67']['10'] = 'Dhe thonë: “Sikur të kishim dëgjuar dhe pasur mend, ne nuk do të ishim ndër banuesit e zjarrit”.';
$TRANSLATE['67']['11'] = 'Pra, i pranojmë mëkatet e veta; banuesit e zjarrit qofshin larg mëshirës.';
$TRANSLATE['67']['12'] = 'Ata, që pa parë i frikësoheshin Zotit të vet, për ta ka falje dhe shpërblim të madh.';
$TRANSLATE['67']['13'] = 'E, ju fshihnie ose thhonie fjalën tuaj haptas; Ai s’ka dyshim se e di çka fshehni në zemrat tuaja.';
$TRANSLATE['67']['14'] = 'A nuk e di Ai që ka krijuar, kur dihet se Ai dëpërton në thellësi të sekreteve, i njh hollësitë.';
$TRANSLATE['67']['15'] = 'Ai është që juve tokën ua bëri të përshtatshme, andaj, ecni nëpër pjesë të saj dhe shfrytëzoni begatitë e Tij, meqë vetëm te Ai është e ardhmja.';
$TRANSLATE['67']['16'] = 'A u garantuat j prej Atij që është në qiell, që të mos ju shafit (të mos u lëshoj) toka kur të dridhet.';
$TRANSLATE['67']['17'] = 'A u garantuat ju prej Atij që është në qiell, që të mos lëshojë kundër jush ndonjë stuhi me rrebesh gurësh. E pra, do ta kuptoni se si është ndëshkimi Im!';
$TRANSLATE['67']['18'] = 'Edhe ata që ishin para tyre përgënjeshtruan, por çfarë ishte dënimi Im?';
$TRANSLATE['67']['19'] = 'A nuk i shikuan ata shpezët përmbi ta krahëhapur dhe kur krahët i palojnë, ato nuk i mban në ajër pos Fuqiplotit. Ai është që çdo send e sheh dhe e di.';
$TRANSLATE['67']['20'] = 'Kush është ajo ushtëri juaja që do t’ju ndihmojë, pos Mëshiruesit? Është e vërtetë se jobesimtarët gjenden në një mashtrim të madh.';
$TRANSLATE['67']['21'] = 'Kush është ai që ju ushqen, po që se Ai ua ndërpret furnizi, im e vet? Jo, askush, por ata vazhdojnë me arrogancë e kundërvënie.';
$TRANSLATE['67']['22'] = 'A është më i udhëzyar ai që ecën i përmbysur me fytyrë në tokë, apo ai që ecën në pozicion qëndrues rrugës së drejtë?';
$TRANSLATE['67']['23'] = 'Thuaj: “Ai është q ju krijoi, ju dhuroi të dëgjuarit, të parit dhe mendjen, kurse pak po falënderoni”';
$TRANSLATE['67']['24'] = 'Thuaj: “Ai është që ju krijoi e ju shumoi në tokë dhe vetëm te Ai do të tuboheni!”';
$TRANSLATE['67']['25'] = 'E, ata po thonë: “Kur do të sëndertohet ky premtim, po qe se jeni të vërtetë?”';
$TRANSLATE['67']['26'] = '. Thuaj: “Atë e di vetëm All-llahu, e unë jam një qortues që ju bën me dije!”';
$TRANSLATE['67']['27'] = 'Kur ta shohin atë (Xhehennemin) se po u afrohet, fytyrat e atyre që nuk besuan u shëmtohendhe u thuhet: “Ky është ai që ju e kërkonit sa më shpejt!”';
$TRANSLATE['67']['28'] = 'Thuaj: “Më tregoni pra, nëse All-llahu më merr shpirtin mua dhe atyre që janë me mua, ose na mëshiron, kush do t’i mbrojë jobesimtarët prej një dënimi të mundimshëm?”';
$TRANSLATE['67']['29'] = 'Thuaj: “Ai është Mëshiruesi, Atij i kemi besuar dhe vetë te Ai jemi mbështetur, kurse ju do takuptoni se kush njëmend është ai i humburi!”';
$TRANSLATE['67']['30'] = 'Thuaj: “Më tregoni, nëse uji juaj humbet në thellësi, kush do t’ju sjell ujë mbitokësor (burimor)?”';
$TRANSLATE['68']['1'] = 'Nun, betohem në pendën dhe në atë çka shkruajnë!';
$TRANSLATE['68']['2'] = 'Ti, me dhuratën pejgamber) e Zotit tënd, nuk je i çmendur!';
$TRANSLATE['68']['3'] = 'Dhe se ti, padyshim do të kesh shpërblim të pandërprerë!';
$TRANSLATE['68']['4'] = 'Vërtet, ti je në një shkallë të lartë morali!';
$TRANSLATE['68']['5'] = 'Më vonë ti do të shohësh, edhe ata do të shohin.';
$TRANSLATE['68']['6'] = 'Se cili prej jush është i çmendur?';
$TRANSLATE['68']['7'] = 'S’ka dyshim se Zoti yt është Ai që e di më së miri se kush është ai që ka humbur prej rrugës së Tij dhe Ai e di më së miri për të udhëzuarit.';
$TRANSLATE['68']['8'] = 'Andaj, ti mos u pajto me gënjeshtarët!';
$TRANSLATE['68']['9'] = 'Ata kanë dëshirë që ti të bësh lajka.';
$TRANSLATE['68']['10'] = 'Mos e respekto askë që betohet shumë dhe është i poshtër!';
$TRANSLATE['68']['11'] = 'që është përgojues e ban fjalë ndër njerëz.';
$TRANSLATE['68']['12'] = 'Shumë koprrac ndaj rrugës së mbarë, i pafrenueshëm, mëkatarë,';
$TRANSLATE['68']['13'] = 'që është shumë i vrazhdë, më në fund edhe kopil (nuk dihet baba).';
$TRANSLATE['68']['14'] = '(Mos e respekto) Vetëm pse ka pasuri e djem!';
$TRANSLATE['68']['15'] = 'Kur atij i lexohen ajetet Tona, ai thotë: “Legjenda të të parëve!”';
$TRANSLATE['68']['16'] = 'Ne do ti dëmkosim atë në turi (në hundë).';
$TRANSLATE['68']['17'] = 'Ne i sprovuam ata (mekasit) sikurse i kemi pas sprovuar pronarët e kopshtit. Kur u betuan se do të vjelin atë herët në agim.';
$TRANSLATE['68']['18'] = 'E nuk thanë (kur u betuan): “Në dashtë All-llahu - inshaell -llahu!”';
$TRANSLATE['68']['19'] = 'E, sa qenë ata fjetur, atë (kopshtin) e goditi një bela nga Zoti yt.';
$TRANSLATE['68']['20'] = 'Dhe ai gdhiu si të ishte i vjelë.';
$TRANSLATE['68']['21'] = 'Ata në mëngjes thërritnin njëri-tjetrin.';
$TRANSLATE['68']['22'] = 'Ngrihuni herët te prodhimet e tuaja, nëse doni t’i vilni!';
$TRANSLATE['68']['23'] = 'Dhe ata vajtënduke pëshpëritur në mes vete (të mos i dëgjojnë kush).';
$TRANSLATE['68']['24'] = 'Të mos lejojnë të afrohet sot në të asnjë i varfër.';
$TRANSLATE['68']['25'] = 'Të mos lejojnë të afrohet sot në të asnjë i varfër.';
$TRANSLATE['68']['26'] = 'E kur e panë atë, thanë: “S’ka dyshim, ne kemi humbur (rrugën)”.';
$TRANSLATE['68']['27'] = 'Jo, (ne nuk kemi humbur rrugën) por pakemi mbetur pa të!';
$TRANSLATE['68']['28'] = 'Ai me maturi prej tyre tha: “A nuk ju thahë përse nuk e përmendi Zotin?”';
$TRANSLATE['68']['29'] = 'Atëherë ata thanë: “I pafajshëm është Zoti ynë, ne ishim të padrejtë”';
$TRANSLATE['68']['30'] = 'Dhe u kthyen e po e qortojën njëri-tjetrin.';
$TRANSLATE['68']['31'] = 'Thanë: “Të mjerët ne, vërtet ne ishim jashtë rrugës!”';
$TRANSLATE['68']['32'] = 'Shpresohet se Zoti yt do na zëvendsojë me ndonjë edhe më të mirë se ai; ne në të vetëm te Zoti ynë mbajmë shpresën.';
$TRANSLATE['68']['33'] = 'Ja, kështu do të jenë dënimi (i mekasve), ndërsa dënimi i botës tjetër do të jetë shumë më i ashpër, sikur të jenë që e kuptojnë.';
$TRANSLATE['68']['34'] = 'Është e sigurt se për të devotshmit do të ketë te Zoti i tyre kopshte të begatshme.';
$TRANSLATE['68']['35'] = 'A do ti bëjmë krminelët të barabartë me mylimanët?';
$TRANSLATE['68']['36'] = 'ç’është me ju, si gjykoni ashtu?';
$TRANSLATE['68']['37'] = 'A mos keni ndonjë libër, e prej saj mësoni?';
$TRANSLATE['68']['38'] = 'E ju do të keni atje çka të dëshironi?';
$TRANSLATE['68']['39'] = 'Amos keni ndonjë zotim të fortë prej Nesh që vazhdon deri në kijamet, se do ta rrini atë që dëshironi?';
$TRANSLATE['68']['40'] = 'Pyeti ata se cili është prej tyre që u garanton atë?';
$TRANSLATE['68']['41'] = 'Apo ata kanë ortakë (zota) që u garantojnë, pra le t’i sjellin ortakët e vet, nëse e thonë të vëtetën?';
$TRANSLATE['68']['42'] = '(Përkujto) Ditën kur ashpërsohet çshtja deri në kulm (në kijamet), e ata do të ftohen për të bërë sexhde, po nuk mundën.';
$TRANSLATE['68']['43'] = 'Shikimet e tyre janë të përulura dhe ata i kaplon poshtërimi, kur dihet se ata kanë qenë të ftuar të bëjnë sexhde sa ishin të shëndoshë (e ata - talleshin).';
$TRANSLATE['68']['44'] = 'Pra më lë Mua dhe ata që e mohojnë këtë Kur’an. Ne do t’i afrojmë ata dalëngadalë te dënimi prej nga nuk presin.';
$TRANSLATE['68']['45'] = 'Dhe atyre Unë u jap afat, sepse ndëshkimi Im ëstë shumë i fortë.';
$TRANSLATE['68']['46'] = 'A mos kërkon ti prej tyre shpërblim, e ata janë të ngarkuar me dhëniet?';
$TRANSLATE['68']['47'] = 'A mos te ta është e fshehta (e Lehvi Mahfudhit), e ata prej atu trumbetojnë (se janë të mirë)?';
$TRANSLATE['68']['48'] = 'Po ti, bëhu i durueshëm ndaj caktimit të Zotit tënd, e mos u bë si ai peshkut në kohën kur pat thirrur (Zotin) dhe ishte i mllefosur.';
$TRANSLATE['68']['49'] = 'Sikur të mos i afrohej atij mëshirë nga Zoti i tij, do të hidhej në shkretërirë i demoralizuar.';
$TRANSLATE['68']['50'] = 'Po Zoti i tij, e bëri atë të zgjedhur prej të mirëve.';
$TRANSLATE['68']['51'] = 'E ata që nuk besuan gati sa të zhdukin me shikimet e tyre, kur dëgjuan Kur’anin, e (nga inati) thanë: “Ai është i marrë!”';
$TRANSLATE['68']['52'] = 'Po ai (Kur’ani) nuk është tjetër vetëm se këshillë për botën (e njerëzve e të xhinëve).';
$TRANSLATE['69']['1'] = 'Momenti i katastrofës së përgjithshme (kijameti).';
$TRANSLATE['69']['2'] = 'ç’është kijameti?';
$TRANSLATE['69']['3'] = 'çka të mësoi ty se ç’është kijameti?';
$TRANSLATE['69']['4'] = 'Themudi dhe Adi përgënjeshtruan tronditjen (kijametin).';
$TRANSLATE['69']['5'] = 'Sa i përket Themudit, ata u shkatërruan me krismë të tmerrshme.';
$TRANSLATE['69']['6'] = 'Ndërsa Adi është shkatërruar me një erë të fortë, të pakufishme.';
$TRANSLATE['69']['7'] = 'Atë Ai e lëshoi kundër tyre shtatë net e tetë ditë rresht, kur shihje njerëzit në to të rrëzuar si të ishi trupa hurmash të zgavëruara.';
$TRANSLATE['69']['8'] = 'A e sheh prej tyre ndonjë që ka mbetur?';
$TRANSLATE['69']['9'] = 'Erdhi faraoni dhe ata që ishin para tij si dhe të përmbysurit (fshatrat e popullit të Lutit), me punë të gabuara.';
$TRANSLATE['69']['10'] = 'Ata kundërshtuan të dërguarin e Zotit të tyre, e Ai i dënoi ata me një dënim edhe më të madh (prej dënimeve të mëparshëm).';
$TRANSLATE['69']['11'] = 'Pasi që uji pat vërshuar, Ne ju bartëm në anije.';
$TRANSLATE['69']['12'] = 'Për ta bërë atë (ngjarje) përkujtim për ju dhe për ta ruajtur atë veshët që janë përkujtues.';
$TRANSLATE['69']['13'] = 'E kur të fryhet në surë një e fryeme.';
$TRANSLATE['69']['14'] = 'Dhe të ngrihen toka e kodrat e t’i mëshojnë njëra-tjetrës me një të goditur.';
$TRANSLATE['69']['15'] = 'Në aë kohë ndodh e vërteta (kijameti).';
$TRANSLATE['69']['16'] = 'E qielli çahet, pse ai atë ditë është i raskapitur.';
$TRANSLATE['69']['17'] = 'Dhe engjëjt qëndrojnë në skajet (anët) e tij, e atë ditë, tetë (engjëj) e bartin mbi vete Arshin e Zotit tënd.';
$TRANSLATE['69']['18'] = 'Atë ditë ju do të ekspozoheni (para Zotit), duke mos mbetur fshehtë asnjë sekret juaji.';
$TRANSLATE['69']['19'] = 'Kujt t’i jepet libri i vet nga e djathta e tij, ai do të thotë: “O ju, qe, lexonie librin tim!”';
$TRANSLATE['69']['20'] = 'Unë kam qenë i bindur se do të jap llogarinë time.';
$TRANSLATE['69']['21'] = 'Dhe ai atëherë është në një jetë të kënaqshme.';
$TRANSLATE['69']['22'] = 'Në një Xhennet të lartë.';
$TRANSLATE['69']['23'] = 'Pemët e tij i ka krejt afër.';
$TRANSLATE['69']['24'] = '(E thuhet) Hani e pini shijshëm, ngase në ditët e kaluara ju e përgatitët këtë.';
$TRANSLATE['69']['25'] = 'Ndërkaq, kujt i jepen librat e veta nga e majta, ai thotë: “O i mjeri unë, të mos më jepej fare libri im.';
$TRANSLATE['69']['26'] = 'Dhe të mos dijsha fare se çka është llogaria ime.';
$TRANSLATE['69']['27'] = 'Ah, sikur të kishte qenë ajo (vdekja e parë) mbarim i amshueshëm për mua.';
$TRANSLATE['69']['28'] = 'Pasuria ime nuk më bëri fare dobi.';
$TRANSLATE['69']['29'] = 'U hoq prej meje çdo kompetencë imja.';
$TRANSLATE['69']['30'] = 'U thuhet zebanive: Kapnie atë, vënja prangat!';
$TRANSLATE['69']['31'] = 'Pastaj atë shtinie në Xhehennem...';
$TRANSLATE['69']['32'] = 'Mandej, lidhne atë me një zingjirë të gjatë shtatëdhjetë kutë.';
$TRANSLATE['69']['33'] = 'Pse ai ka qenë që nuk besoi All-llahun e madhëruar.';
$TRANSLATE['69']['34'] = 'Ai nuk nxiste për t’i ushqyer të varfërit.';
$TRANSLATE['69']['35'] = 'Ai sot nuk ka këtu ndonjë mik.';
$TRANSLATE['69']['36'] = 'As ushqim tjetër përveç të të shplarave.';
$TRANSLATE['69']['37'] = 'Që atë nuk e ha kush, pos mëkatarëve.';
$TRANSLATE['69']['38'] = 'Pra Unë betohem në atë që e shihni!';
$TRANSLATE['69']['39'] = 'Edhe në atë që nuk e shihni!';
$TRANSLATE['69']['40'] = 'Se me të vërtetë ai (Kur’ani) është fjalë (e Zotit) që e lexon i dërguari i ndershëm.';
$TRANSLATE['69']['41'] = 'Ai nuk është fjalë e ndonjë poeti, po ju nuk besoni.';
$TRANSLATE['69']['42'] = 'Nuk është as fjalë e ndonji falltori, po ju nuk besoni.';
$TRANSLATE['69']['43'] = 'Është zbritje prej Zotit të botëve!';
$TRANSLATE['69']['44'] = 'Sikur të trillonte ai (Muhammedi) për Ne ndonjë fjalë!';
$TRANSLATE['69']['45'] = 'Ne do ta kapim atë me fuqinë Tonë.';
$TRANSLATE['69']['46'] = 'E pastaj do t’ia këputnim atij arterien e zemrës.';
$TRANSLATE['69']['47'] = 'Askush prej jush nuk do të mund të ndërhynte për mbrojtjen e tij.';
$TRANSLATE['69']['48'] = 'Ai (kur’ani) është këshillë për të ruajturit.';
$TRANSLATE['69']['49'] = 'S’ka dyshim se Ne dijmë që prej jush ka qe e përgënjeshtrojnë.';
$TRANSLATE['69']['50'] = 'Ai (Kur’ani) njëmend do të jetë dëshprim për jobesimtarët.';
$TRANSLATE['69']['51'] = 'Ai është e vërtetë bindëse!';
$TRANSLATE['69']['52'] = 'Andaj, ti lartësoje nga të metat Zotin tënd të madhëruar!';
$TRANSLATE['70']['1'] = 'Një lutës e kerkoi dënimin e pashmangshëm,';
$TRANSLATE['70']['2'] = 'për jobesimtarët. Atë (dënim) s’ka kush që mund ta ndalë.';
$TRANSLATE['70']['3'] = 'Ai dënim vjen nga All-llahu, pronari ishkallëve të larta (në qiej).';
$TRANSLATE['70']['4'] = 'Atje ngjiten engjëjt dhe shpirti (Xhibrili) në një ditë që zgjatë pesëdhjetë mijë vjet (ose lartësia e atyre shkallëve është pesëdhjetë mijë vjet).';
$TRANSLATE['70']['5'] = 'Ti, pra, duro me një durim pa ankesë (të mirë).';
$TRANSLATE['70']['6'] = 'Atyre u duket ai larg,';
$TRANSLATE['70']['7'] = 'Kurse Neve ai na duket afër.';
$TRANSLATE['70']['8'] = 'Dita kur qielli të bëhet si kallaji i shkrirë.';
$TRANSLATE['70']['9'] = 'Kodrat të bëhen si leshi i lënurur.';
$TRANSLATE['70']['10'] = 'Dhe asnjë mik nuk pyet për mikun.';
$TRANSLATE['70']['11'] = 'Edhe pse ata shihen ndërmjet vete (edhe njihen, por ikin prej njëri-tjetrit). Krimineli dëshiron sikur të kishte paguar dënimin e asaj dite me bijtë e vet.';
$TRANSLATE['70']['12'] = 'Dhe me gruan e vet dhe me vëllain e vet.';
$TRANSLATE['70']['13'] = 'Edhe me të afërmit e tij që ai te ata mbështetej.';
$TRANSLATE['70']['14'] = 'Edhe me krejt çka ka në tokë, e vetëm të kishte shpëtuar!';
$TRANSLATE['70']['15'] = 'Jo, kurrsesi, ajo (para) është Ledhdha - flaka (emër i Xhehennemit).';
$TRANSLATE['70']['16'] = 'Është që heq kapakët e kokës.';
$TRANSLATE['70']['17'] = 'Që e thërret (tërheq prej vetes) atë që është zbbrapsur e larguar (prej besimit).';
$TRANSLATE['70']['18'] = 'Dhe që ka tubuar (pasuri) dhe e ka ruajtur (fshehur).';
$TRANSLATE['70']['19'] = 'Është e vërtetë se njeriu është i prirur të jetë i padurueshëm.';
$TRANSLATE['70']['20'] = 'Pse kur e godit ndonjë e keqe, ai ankohet së tepërmi.';
$TRANSLATE['70']['21'] = 'Ndërkaq, kur e gjen e mira, ai bëhet tepër koprac.';
$TRANSLATE['70']['22'] = 'Përveç atyre që falen,';
$TRANSLATE['70']['23'] = 'të cilët janë të rregullt në faljen e namazit të tyre.';
$TRANSLATE['70']['24'] = 'Dhe ata që në pasurinë e vet kanë ndarë në një pjesë të caktuar.';
$TRANSLATE['70']['25'] = 'Për lypësin dhe për nevojtarin që nuk lyp.';
$TRANSLATE['70']['26'] = 'Edhe ata që e besojnë bindshëm ditën e gjykimit.';
$TRANSLATE['70']['27'] = 'Edhe ata që i frikësohen dënimit nga Zoti i tyre.';
$TRANSLATE['70']['28'] = 'Sepse është e vërtetë që nuk ka shpëtim prej dënimit nga Zoti i tyre.';
$TRANSLATE['70']['29'] = 'Edhe ata që janë ruajtur prej punëve të ndyra (amorale).';
$TRANSLATE['70']['30'] = 'Me përjashtim ndaj grave të veta dhe ndaj robëreshave që i kanë në posedim, ata nuk qortohen për to.';
$TRANSLATE['70']['31'] = 'E kush kërkon përveç tyre, të tillët janë të shfrenuar (meritojnë dënim).';
$TRANSLATE['70']['32'] = 'Edhe ata që janë besnikë ndaj amanetit që u është besuar dhe ndaj premtimit të dhënë.';
$TRANSLATE['70']['33'] = 'Edhe ata që dëshminë e vet e zbatojnë (nuk e mohojnë, nuk e fshehin).';
$TRANSLATE['70']['34'] = 'Edhe ata që janë të kujdesshëm ndaj rregullave të namazit të tyre.';
$TRANSLATE['70']['35'] = 'Të tillët janë në Xhennete dhe janë të nderuar.';
$TRANSLATE['70']['36'] = 'ç’është me ata që nuk besuan dhe zgjasin qafat nga ti?';
$TRANSLATE['70']['37'] = 'Ulën në grupe në të djathtë e në të majtë teje?';
$TRANSLATE['70']['38'] = 'A dëshiron secili prej tyre të hyjë në Xhennetin r begatshëm?';
$TRANSLATE['70']['39'] = 'Kurrsesi! Ne i krijuam ata nga ajo çka ata e dinë.';
$TRANSLATE['70']['40'] = 'Pra betohem në Zotin e lindjeve e të perëndimeve, se Ne kemi fuqi.';
$TRANSLATE['70']['41'] = 'të zëvendësojmë me më të mirë se ata dhe Ne nuk mund të na dalë kush para.';
$TRANSLATE['70']['42'] = 'Po ti lëri ata të zhyten edhe më thellë në të kota dhe të dëfrehen deri që të ballafaqohen në ditën e tyre që po u premtohet.';
$TRANSLATE['70']['43'] = 'Ditën kur duke u ngutur dalin prej varreve, sikur nguteshin te idhujt,';
$TRANSLATE['70']['44'] = 'Me shikimet e tyre të përulura i ka kapluar poshtërimi. Ajo është dita që ka qenë premtuar.';
$TRANSLATE['71']['1'] = 'Ne e dërguam Nuhun e populli i vet (dhe i thamë): “Tërhiqja vërejtjen popullit tënd para se ata t’i godasë dënimi i rëndë!”';
$TRANSLATE['71']['2'] = 'Ai tha: “O populli im, unë jam i dëruar te ju, jam i qartë.';
$TRANSLATE['71']['3'] = 'Adhuronie All-llahun, kini frikë prej Tij dhe mua më dëgjoni.';
$TRANSLATE['71']['4'] = 'Ai u falë juve nga mëkatet tuaja dhe ua vazhdon jetën deri në një afat të caktuar (pa u dënuar), e kur të vijë afati i caktuar prej All-llahut, ai nuk shtyhet për më vonë, nëse jeni që e dini”.';
$TRANSLATE['71']['5'] = 'Ai tha: “O Zoti im, unë e thirra popullin tim natën e ditën.';
$TRANSLATE['71']['6'] = 'Por thirrja ime vetëm ua shtoi ikjen.';
$TRANSLATE['71']['7'] = 'Dhe sa herë që unë i thirritja ata për t’u falur Ti mëkatet, ata i vënin gishtat e tyre në veshë dhe e mbulonin kokat me teshat e tyre dhe vazhdonin në atë të tyren me një mendjemadhësi të fortë.';
$TRANSLATE['71']['8'] = 'Pastaj, unë i kam thirrur ata haptazi.';
$TRANSLATE['71']['9'] = 'Pastaj unë thirrjen ua drejtoja atyre haptazi, e shumë herë edhe fshehurazi”.';
$TRANSLATE['71']['10'] = 'Unë u thashë: “Kërkoni falje Zotit tuaj, se Ai vërtet falë shumë;';
$TRANSLATE['71']['11'] = 'Ai ju lëshon nga qielli shi me bollëk,';
$TRANSLATE['71']['12'] = 'Ju shumon pasurinë dhe fëmijët, ju bën të keni kopshte dhe ju jep lumenj.';
$TRANSLATE['71']['13'] = 'ç’është me ju që All-llahut nuk i shprehni madhërinë që e meriton?';
$TRANSLATE['71']['14'] = 'Kur Ai ju krijoi në disa etapa.';
$TRANSLATE['71']['15'] = 'A nuk e keni parë se si All-llahu krijoi shtatë palë qiej (në kate).';
$TRANSLATE['71']['16'] = 'Dhe në ta hënën e bëri dritë, diellin e bëri ndriçues.';
$TRANSLATE['71']['17'] = 'Dhe All-llahu u shpiku juve prej tokës si bimë.';
$TRANSLATE['71']['18'] = 'Pastaj ju kthen në të pa dyshim ju nxjerr sërish.';
$TRANSLATE['71']['19'] = 'All-llahu juve ua bëri tokën të sheshtë.';
$TRANSLATE['71']['20'] = 'Që nëpër të të ecni rrugëve të gjera”.';
$TRANSLATE['71']['21'] = 'Nuhu tha: “Zoti im, ata më kundërshtuan, ata shkuan pas atyre (pasanikëve), të cilëve pasuria dhe fëmijët e tyre vetëm ua shtuan dëshpërimin.';
$TRANSLATE['71']['22'] = 'Dhe ata (pasanikët) u kurdisën atyre kurthe shumë të mëdha”.';
$TRANSLATE['71']['23'] = 'Dhe u thanë: Mos braktisni zotat tuaj kurrsesi, mos braktisni Vedda-në, as Suvva-në, e Jeguth-in, Jeukë-n e Nesre-n.';
$TRANSLATE['71']['24'] = 'Dhe ata i bënë të humbur shumë sish, e Ti zullumqarëve mos u jep tjetër, por vetëm humbje.';
$TRANSLATE['71']['25'] = 'Për shkak të mëkateve të tyre ata u përmbytën dhe menjëherë u futën në zjarr dhe ata, përveç All-llahut, nuk gjetën ndihmëtarë.';
$TRANSLATE['71']['26'] = 'E Nuhu tha: “O Zoti im, mos lë mbi tokë asnjë nga jobesimtarët!';
$TRANSLATE['71']['27'] = 'Sepse, nëse Ti i lë ata, robërit Tu do t’i humbin dhe prej tyre nuk lind tjetër, vetëm të prishur e jobesimtarë.';
$TRANSLATE['71']['28'] = 'Zoti im, më falë mua dhe prindërit e mi, dhe ata që hynë në shtëpinë time, duke qenë besimtarë. edhe besimtarët e besimtaret, ndërsa jobesimtarëve mos u shto tjetër, vetëm dëshpërim”.';
$TRANSLATE['72']['1'] = 'Thuaj: “Mua më shpalllet se një grup i vogël nga xhinët i vuri veshin dhe dëgjoi (Kur’anin) dhe (kur u kthyen te të vetët) thanë: “Ne kemi dëgjuar një Kur’an që mahnit,';
$TRANSLATE['72']['2'] = 'që udhëzon në të vërtetën, andaj ne i besuam atij dhe Zotit tonë, kurrsesi nuk do t’ia shoqërojmë më akë”';
$TRANSLATE['72']['3'] = 'Dhe se latësua e madhëruar e Zotit tonë, nuk është që ka as grua, as fëmijë.';
$TRANSLATE['72']['4'] = 'S’ka dshim se ai mendjelehti jonë fliste të pavërteta ndaj All-llahut.';
$TRANSLATE['72']['5'] = 'Ndërsa, ne kemi pas menduar se as njerëzit, as xhinët nuk flasin gënjeshtra përkitazi me All-llahun,';
$TRANSLATE['72']['6'] = 'Dhe se ka pasur burra prej njerëzve, që kërkonin ndihmë prej disa xhinëve dhe ashtu ua shtonin atyre edhe më shumë arrogancën.';
$TRANSLATE['72']['7'] = 'Dhe se ata (njerëzit) kanë menduar, siç keni menduar edhe ju, se All-llahu nuk ka për të ringjallur askë.';
$TRANSLATE['72']['8'] = 'Dhe vërtet, ne kemi hulumtuar ta arrijnë qiellin, po atë e gjetëm përplot roje të forta dhe me shkëndija (zjarri që djeg),';
$TRANSLATE['72']['9'] = 'Dhe ne më parë hulumtonim në vendëvështrim për të dëgjuar, e kush përpiqet të dëgjojë tash, ai has në pusinë e shkëndijës së zjarrtë.';
$TRANSLATE['72']['10'] = 'Ne nuk e dimë se me këtë (përgaditje të qiellit) është për qëllim ndonjë e keqe për ata që janë në tokë, apo Zoti i tyre dëshiron që ata t’i udhëzojë.';
$TRANSLATE['72']['11'] = 'Është e vërtetë se prej nesh ka të mirë, por ka edhe që nuk janë aq të mirë, sepse ne kemi qenë në drejtime të ndryshme.';
$TRANSLATE['72']['12'] = 'Dhe ne kemi qenë të bindur se All-llahut nuk mund t’i shmangeni në tokë e as nuk mund t’i shpëtojmë Atij duke ikur.';
$TRANSLATE['72']['13'] = 'Dhe ne, sapo e dëgjuam udhëzimin (Kur’anin), i besuam atij, e kush e beson Zotit të vet, ai nuk i frikësohet ndonjë dënimi e as ndonjë padrejtësie.';
$TRANSLATE['72']['14'] = 'Është e vërtetë se prej nesh ka besimtarë (myslimanë) dhe prej nesh ka që janë jashtë rrugës (jobesimtarë), e kush e pranoi islamin, të tillët mësynë rrugën e shpëtimit.';
$TRANSLATE['72']['15'] = 'Ndërsa, ata që lëshuan rrugën, ata u bënë lëndë e Xhehennemit!';
$TRANSLATE['72']['16'] = 'sikur ata (idhujtarët) t’i përmbaheshin rrugës së drejtë, Ne do t’iu lëshonim atyre ujë me bollëk,';
$TRANSLATE['72']['17'] = 'Për të sprovuar me të. E kush ia kthen shpinën adhurimit ndaj Zotit të vet, Ai e shpie atë në vuajtje të padurueshme.';
$TRANSLATE['72']['18'] = 'Dhe (mua më shpallet) e vërtetë xhamitë janë veçant për të adhuruar All-llahun, e mos adhuroni në to askë tjetër me All-llahun!';
$TRANSLATE['72']['19'] = 'Dhe se kur u ngrit robi i All-llahut ta adhurojë Atë, ata (xhinët ose idhujtarët) gati hipnin njëri mbi tjetërin për të dagjuar.';
$TRANSLATE['72']['20'] = 'Thuaj: “Unë dëshiroj vetëm Zotin tim dhe Atij nuk i bëj asnjë shok!”';
$TRANSLATE['72']['21'] = 'Thuaj: “Unë nuk kam në dorë që t’ju largojë dëmin, as t’ju sjell dobi!”';
$TRANSLATE['72']['22'] = 'Thuaj: “Është e vërtetë se mua nuk më mbron askush prej (dënimit) të All-llahut dhe përveç Tij, unë nuk gjej mbështetje”.';
$TRANSLATE['72']['23'] = '(Nuk kam në dorë tjetër), përveç kumtimin prej All-llahut dhe përveç shpalljeve të Tij. Kush e kundërshton All-llahun dhe të dërguarin e tij, i tij është zjarri i Xhehennemit, aty do të jenë përgjithmonë, pambarim.';
$TRANSLATE['72']['24'] = 'Derisa kur ta shohin atë që u premtohet, atëherë do të kuptojnë se kush është më i dobët në ndihmë dhe më i vogël në numër.';
$TRANSLATE['72']['25'] = 'Thuaj: “Unë nuk e di a do të jetë afër ajo që u premtohet, apo Zoti im do ta vonojë atë shumë?”';
$TRANSLATE['72']['26'] = 'Ai është që e di të fshehtën, por fshehtësinë e vet nuk ia zbulon askujt.';
$TRANSLATE['72']['27'] = 'Me përjashtim të ndonjë të dërguari që Ai do. Atëbotë Ai vë roje para edhe prapa tij (kur i shpall fshehtësinë),';
$TRANSLATE['72']['28'] = 'Për ta ditur (për t’u dukur) se ata kanë kumtuar shpalljet e Zotit të tyre, dhe se Ai ka përfshi atë te ata, dhe Ai e di sasinë e çdo gjëje që ekziston.';
$TRANSLATE['73']['1'] = 'O ti i mbështjellë!';
$TRANSLATE['73']['2'] = 'Ngrihu për falje natën, përveç një pjese të vogël të saj.';
$TRANSLATE['73']['3'] = 'Gjysmën e saj ose diç më pak se gjysmën.';
$TRANSLATE['73']['4'] = 'Ose shto diç prej saj dhe Kur’anin lexoje me vëmendje.';
$TRANSLATE['73']['5'] = 'Ne do të shpallim ty fjalë të rëndë (të madhe).';
$TRANSLATE['73']['6'] = 'E s’ka dyshim se akiviteti i natës është fuqizues dhe fjlë më e qartë.';
$TRANSLATE['73']['7'] = 'Vërtet, Ti gjatë ditës ke angazhim të gjatë (të madh).';
$TRANSLATE['73']['8'] = 'Po ti përmende Zotin tënd dhe plotësisht përkushtoju Atij!';
$TRANSLATE['73']['9'] = 'Zot i lindjes dhe i perëndimit, nuk ka zot tjetër vetëm Ai, pra Atë merrë për mbështetje.';
$TRANSLATE['73']['10'] = 'Dhe kij durim ndaj asaj që të thonë dhe largohu atyre i matur.';
$TRANSLATE['73']['11'] = 'Dhe më lë Mua për ata gënjeshtarët pasanikë, dhe jepu atyre pak afat.';
$TRANSLATE['73']['12'] = 'Se me të vërtetë te Ne ka pranga e zjarr.';
$TRANSLATE['73']['13'] = 'Edhe ushqim që nuk gëlltitet edhe dënim të dhembshëm.';
$TRANSLATE['73']['14'] = 'Atë ditë kur toka e kodrat të dridhen, e kodrat bëhen rërë rrëshqitëse.';
$TRANSLATE['73']['15'] = 'Ne dërguam te ju të dërguar që dëshmon kundër jush, ashtu siç patëm dërguar edhe te faraoni të dërguar.';
$TRANSLATE['73']['16'] = 'E faraoni e kundërshtoi të dërguarin, andaj Ne i dhamë atij një dënim të rëndë.';
$TRANSLATE['73']['17'] = 'Nëse ju mohoni, si do të mbroheni në një ditë që fëmijët do t’i bëjë pleq?';
$TRANSLATE['73']['18'] = 'Atë ditë qielli do të çahet dhe premtimi i Tij do të realizohet.';
$TRANSLATE['73']['19'] = 'Këto janë një përkujtim qortues, e kush do, ai e zgjedh rrugën për te Zoti i vet.';
$TRANSLATE['73']['20'] = 'Zoti yt e di se ti dhe një grup i atyre që janë me ty, kaloni në adhurim më pak se dy të tretat e natës ose gjysmën e saj a një të tretën e saj. All-llahu e di gjatësinë e natës dhe të ditës dhe Ai e diti që ju nuk do mund t’i përmbaheni asaj, andaj u lehtësoi juve, pra falnu duke lexuar Kur’an sa t’ju vijë më lehtë prej namazit. Ai e diti se nga ju do të ketë të sëmurë dhe të tjerët që gjallërojnë nëpër tokë dke kërkuar nga begatitë e All-llahut, e dhe të tjerë që luftojnë në rrugën e All-llahut, pra falnu dhe lexoni sa të jetë më lehtë për tij dhe kryene me rregull faljen, jepne zeqatin, huazoni (jepni sadaka) për hir të All-llahut hua të mirë, se çka të përgatitni për veten tuaj prej të mirave, atë do ta gjeni te All-llahu edhe më të mirë, dhe shpërblim më të madh, e kërkoni falje prej All-llahut, se All-llahu është mëkatfalës, mëshirues!';
$TRANSLATE['74']['1'] = 'O ti i mbuluar!';
$TRANSLATE['74']['2'] = 'Ngrihu dhe tërhiqu vërejtjen (duke i thirrur).';
$TRANSLATE['74']['3'] = 'Dhe madhëroje Zotin tënd!';
$TRANSLATE['74']['4'] = 'Dhe rrobat tua pastroji!';
$TRANSLATE['74']['5'] = 'Dhe të keqës së ndyrë largohu!';
$TRANSLATE['74']['6'] = 'Dhe mos u mburr me atë që jep e të duket shumë!';
$TRANSLATE['74']['7'] = 'Dhe për hir të Zotit tënd duro!';
$TRANSLATE['74']['8'] = 'E, kur të fryhet në Sur,';
$TRANSLATE['74']['9'] = 'Atëherë ajo është ditë e vështirë.';
$TRANSLATE['74']['10'] = 'Për jobesimtarët është jo e lehtë.';
$TRANSLATE['74']['11'] = 'Ti, më le Mua dhe atë që e krijova të vetmuar.';
$TRANSLATE['74']['12'] = 'Dhe bëra këtë pasuri të madhe.';
$TRANSLATE['74']['13'] = 'E bëra edhe me djem që i ka me vete.';
$TRANSLATE['74']['14'] = 'Dhe Unë i lash në disponim të gjitha mundësitë.';
$TRANSLATE['74']['15'] = 'E ai lakmon që t’i shtoj edhe më tepër.';
$TRANSLATE['74']['16'] = 'E jo, se ai vërtet ishte mohues i fakteve tona!';
$TRANSLATE['74']['17'] = 'Atë do ta ndrydhi me një dënim të rëndë e të padurueshëm.';
$TRANSLATE['74']['18'] = 'Pse ai shumë mendonte, radhitte e përgatiste (për Kur’anin).';
$TRANSLATE['74']['19'] = 'Qoftë i shkatërruar, po si përceptonte?';
$TRANSLATE['74']['20'] = 'Prapë qoftë i shkatërruar, po si përceptonte?';
$TRANSLATE['74']['21'] = 'Ai sërish thellohej në mendime.';
$TRANSLATE['74']['22'] = 'Pastaj mrrolej dhe bëhej edhe i vrazhdë.';
$TRANSLATE['74']['23'] = 'Pastaj ktheu shpinën me mendjemadhësi.';
$TRANSLATE['74']['24'] = 'E tha: “Ky (Kur’ani) nuk është tjetër, vetëm se magji që përcillet prej të tjerëve.';
$TRANSLATE['74']['25'] = 'Ky nuk është tjetër, përveç fjalë njerëzish!”';
$TRANSLATE['74']['26'] = 'E Unë atë do ta hedhë në Sekar!';
$TRANSLATE['74']['27'] = 'E, ku e di ti se çka është Sekar?';
$TRANSLATE['74']['28'] = 'Ai nuk lë send të mbetet pa e djegur.';
$TRANSLATE['74']['29'] = 'Ai është që ua prish dhe ua nxin lëkurat.';
$TRANSLATE['74']['30'] = 'Mbikëqyrës mbi të janë nëntëmbëdhjetë.';
$TRANSLATE['74']['31'] = 'E mbiqëkyrës të zjarrit, Ne nuk bëjmë tjetër, përveç engjëjve dhe numrin e tyre (të përmendur në Kur’an) nuk e bëmë për tjetër, por vetëm si sprovë për ata që nuk besuan, e që të binden ata që u është dhënë libri, e atyre që besuan t’u shtohet besimi, e atyre që u është dhëmë libri dhe besimtarët të mos kenë dyshim, dhe ata që zemrat i kanë të sëmura dhe jobesimtarët të thonë: “çka dashti All-llahu me këtë shembull?” Ja ashtu, All-llahu e lë të humbur atë që do, e ushtrinë e Zotit tënd nuk e di kush pos Atij, e ai (Sekari), nuk është tjetër vetëm se një përkujtim për njerëz.';
$TRANSLATE['74']['32'] = 'Jo, nuk është ashtu, Pasha hënën!';
$TRANSLATE['74']['33'] = 'Edhe natën kur tërhiqet!';
$TRANSLATE['74']['34'] = 'Edhe agimin kur agon!';
$TRANSLATE['74']['35'] = 'S’ka dyshim se ai (Sekari) është një nga belatë më të mëdha.';
$TRANSLATE['74']['36'] = 'Është vërejtje për njerëz!';
$TRANSLATE['74']['37'] = 'Për atë nga mesi juaj që dëshiron të përparojë (në të mira) apo të ngecë.';
$TRANSLATE['74']['38'] = 'Secili njeri është pengë i veprës së vet.';
$TRANSLATE['74']['39'] = 'Përveç atyre të djathtëve.';
$TRANSLATE['74']['40'] = 'Që janë në Xhennete e i bëjnë pyetje njëri-tjetrit,';
$TRANSLATE['74']['41'] = 'Përkitazi me kriminelët (e u thonë):';
$TRANSLATE['74']['42'] = '“çka u solli juve në Sekar?”';
$TRANSLATE['74']['43'] = 'Ata thonë: “Nuk kemi qenë prej atyre që faleshin (që bënin namaz);';
$TRANSLATE['74']['44'] = 'Nuk kemi qenë që ushqyenim të varfërit;';
$TRANSLATE['74']['45'] = 'Dhe kemi qenë që përziheshim me të tjerët në punë të kota.';
$TRANSLATE['74']['46'] = 'Dhe kemi qenë që nuk e besonim ditën e gjykimit.';
$TRANSLATE['74']['47'] = 'Derisa na erdhi e vërteta (vdekja)!”';
$TRANSLATE['74']['48'] = 'E tanimë, atyre nuk u bën dobi ndërmjetësimi i ndërmjetësuesve,';
$TRANSLATE['74']['49'] = 'Po çka kishin ata që largohesin prej këshillës (Kur’ait)?';
$TRANSLATE['74']['50'] = 'Ata ishin si gomarë të trembur (të egër).';
$TRANSLATE['74']['51'] = 'Që ikin prej luanit (ose prej gjahtarit).';
$TRANSLATE['74']['52'] = 'Veç kësaj, secili prej tyre dëshironte t’i jepej libër i posaçëm (i hapët, i qartë).';
$TRANSLATE['74']['53'] = 'E jo! Por ata janë që nuk frikësohen botës tjetër.';
$TRANSLATE['74']['54'] = 'Dhe jo! Se ai (Kur’ani) është këshillë e lartë.';
$TRANSLATE['74']['55'] = 'E kush do, këshillohet me të.';
$TRANSLATE['74']['56'] = 'Po ata nuk mund të marrin mësim prej tij, vetëm nëse All-llahu di, Ai është i denjë për t’iu ruajtur dhe i denjë për falje mëkatesh!';
$TRANSLATE['75']['1'] = 'Betohem në ditën e kijametit;';
$TRANSLATE['75']['2'] = 'Betohem në shpirtin që është shumë qortues!';
$TRANSLATE['75']['3'] = 'A mendon njeriu se nuk do t’i tubojmë eshtrat e tij?';
$TRANSLATE['75']['4'] = 'Po, do t’ia tubojmë! Duke qenë se Ne jemi të zotët t’ia rikrijojmë si kanë qenë edhe majat e gishtave të tij!';
$TRANSLATE['75']['5'] = 'Por, njeriu dëshiron të vazhdojë edhe mëtej në mëkate.';
$TRANSLATE['75']['6'] = 'Andaj edhe pyet: “Kur është dita e kijametit?”';
$TRANSLATE['75']['7'] = 'E, kur të merren sytë (të parët)?';
$TRANSLATE['75']['8'] = 'E të zëhet hëna (errësohet),';
$TRANSLATE['75']['9'] = 'Dhe të bashkohet dielli e hëna.';
$TRANSLATE['75']['10'] = 'Atë ditë njeriu do të thotë: “Nga të iket!”';
$TRANSLATE['75']['11'] = 'Jo, nuk ka strehim!';
$TRANSLATE['75']['12'] = 'Atë ditë vetëm te Zoti yt është caku!';
$TRANSLATE['75']['13'] = 'Atë ditë njeriu do të njihet me atë që çoi para dhe me atë që la prapa.';
$TRANSLATE['75']['14'] = 'Po njeriu është dëshmitar i vetvetes.';
$TRANSLATE['75']['15'] = 'Edhe nëse i paraqet arsyetimet e veta.';
$TRANSLATE['75']['16'] = 'Ti (Muhammed) mos shqipto atë (Kur’anin) me gjuhën tënde për ta nxënë atë me të shpejtë!';
$TRANSLATE['75']['17'] = 'Sepse Ne e kemi për detyrë tubimin dhe leximin e tij!';
$TRANSLATE['75']['18'] = 'E kur ta lexojnë atë ty, ti përcille me të dëgjuar leximin e tij.';
$TRANSLATE['75']['19'] = 'Dhe pastaj është obligim i yni që ta shkoqisim atë.';
$TRANSLATE['75']['20'] = 'Jo, nuk është ashtu! Por ju jeni që e doni të ngutshmen (dynjanë).';
$TRANSLATE['75']['21'] = 'Dhe e lini pas shpine atë të ardhmen (ahiretin).';
$TRANSLATE['75']['22'] = 'Atë ditë do të ketë fytyra të shkëlqyera (të gëzuara).';
$TRANSLATE['75']['23'] = 'Që Zotin e tyre e shikojnë.';
$TRANSLATE['75']['24'] = 'Atë ditë ka fytyra edhe të vrazhda.';
$TRANSLATE['75']['25'] = 'Që presin t’u thyhet kurrizi (të shkatërrohen).';
$TRANSLATE['75']['26'] = 'Jo dhe Jo! Po kur të arrijë (shpirti) në gropë të fytit (të gjoksit),';
$TRANSLATE['75']['27'] = 'Dhe thuhet (nga familja e të afërmit): “Kush do ta shërojë?”';
$TRANSLATE['75']['28'] = 'Dhe ai bindet se ai po ndahet.';
$TRANSLATE['75']['29'] = 'E t’i puqet kofsha për kofshe (t’i vështirësohet gjendja).';
$TRANSLATE['75']['30'] = 'Atë ditë vetëm te Zoti yt shkohet.';
$TRANSLATE['75']['31'] = 'E ai as nuk vërtetoi atë që duhej, as nuk u falë.';
$TRANSLATE['75']['32'] = 'Por përgënjeshtroi dhe ktheu shpinën.';
$TRANSLATE['75']['33'] = 'Dhe shkonte te familja e tij me fodullëk.';
$TRANSLATE['75']['34'] = 'Të është afruar ty e keqja e t’u afroftë! (ose i mjeri ti i mjeri).';
$TRANSLATE['75']['35'] = 'Edhe nje herë t’u afrua ty e keqja; t’u afroftë!';
$TRANSLATE['75']['36'] = 'A mos mendon njeriu se do të lihet duke mos zënë asgjë (pa kurrfarë përgjegjësie).';
$TRANSLATE['75']['37'] = 'A nuk ka qenë ai në një pikë ujë që derdhet.';
$TRANSLATE['75']['38'] = 'E pastaj u bë gjak i trashë, e Ai e krijoi dhe e përsosi?';
$TRANSLATE['75']['39'] = 'Dhe prej tij Ai i bëri dy lloje: mashkullin dhe femrën.';
$TRANSLATE['75']['40'] = 'A nuk është Ai (Zot) i fuqishëm që i ngjall të vdekurit?';
$TRANSLATE['76']['1'] = 'Vërtetë ka kaluar një periudhë kohore, që njeriu nuk ekzistonnte fare si një diçka i përmendur.';
$TRANSLATE['76']['2'] = 'Ne e krijuam prej një uji të bashkëdyzuar për të sprovuar atë, andaj e bëmë të dëgjojë e të shohë.';
$TRANSLATE['76']['3'] = 'Ne e udhëzuam atë në rrugën të drejtë, e ai do të jetë: mirënjohës ose përbuzës.';
$TRANSLATE['76']['4'] = 'Ne për jobesimtërët kemi përgatitur zinxhirë, pranga e zjarr.';
$TRANSLATE['76']['5'] = 'S’ka dyshim se të devotshmit do të pijnë nga gota që përziejra brenda saj është nga kafuri (aromatik).';
$TRANSLATE['76']['6'] = 'Është një burim prej të cilit pijnë robërit e All-llahut dhe e bartin atë ku të duan.';
$TRANSLATE['76']['7'] = 'Ata janë që zbatojnë premtimet e tyre dhe i frikësohen një dite dëmi i së cilës ka përmasa të mëdha.';
$TRANSLATE['76']['8'] = 'Ata janë që për hir të Tij u japin ushqim të varvfërve, jetimmëve dhe të zënëve robër.';
$TRANSLATE['76']['9'] = 'Ne po ju ushqejmë vetëm për hir të All-llahut dhe prej jush nuk kërkojmë ndonjë shpërblim e as falënderim.';
$TRANSLATE['76']['10'] = 'Ne i frikësohemi (dënimit) Zotit tonë në një ditë që fytyrat i bën të zymta dhe është sshumë vështirë.';
