$TRANSLATE['89']['22'] = 'Dhe kur të vijë Zoti yt dhe engjëjt qëndrojnë rradhë-rradhë!';
$TRANSLATE['89']['23'] = 'Dhe atë ditë sillet Xhehennemi, ditën kur përkujtohet njeriu, e ç’dobi i bën atij përkujtimi?';
$TRANSLATE['89']['24'] = 'Thotë: “O, i gjori unë, sikur të isha i parapërgatitur për jetën time!”';
$TRANSLATE['89']['25'] = 'Pra, ditën kur nuk dënon si Ai askush.';
$TRANSLATE['89']['26'] = 'Dhe nuk do të farkojë pranga si Ai askush.';
$TRANSLATE['89']['27'] = 'O shpirt i qetësuar!';
$TRANSLATE['89']['28'] = 'Kthehu te Zoti yt i kënaqur (për vehte) e kënaqës (për Zotin)!';
$TRANSLATE['89']['29'] = 'Hyr mes robërve të Mi!';
$TRANSLATE['89']['30'] = 'Dhe hyr në Xhennetin Tim!';
$TRANSLATE['90']['1'] = 'Betohem në këtë qytet!';
$TRANSLATE['90']['2'] = 'E ti je që gjendesh në këtë qytet!';
$TRANSLATE['90']['3'] = 'Betohem në prindin dhe në atë që lind prej tij!';
$TRANSLATE['90']['4'] = 'Vërtet, Ne e krijuam njeriun në vështirësi të njëpasnjëshme.';
$TRANSLATE['90']['5'] = 'A mendon ai, se atë nuk mund ta mposhtë askush?';
$TRANSLATE['90']['6'] = 'E thotë: “Unë kam shpenzuar shumë pasuri!”';
$TRANSLATE['90']['7'] = 'A mendon ai se atë askush nuk e ka parë?';
