$TRANSLATE['90']['14'] = 'Ose dhënia e ushqimit në kohën kur mbretëron uria:';
$TRANSLATE['90']['15'] = 'Ndonjë jetimi që është i afërt,';
$TRANSLATE['90']['16'] = 'Ose ndonjë të varfëri që e ka molisur varfëria.';
$TRANSLATE['90']['17'] = 'E pastaj të bëhej prej atyre që besuan, që këshilluan njëri-tjetrin për durim dhe që këshillluan për mëshirë (për ndihmë).';
$TRANSLATE['90']['18'] = 'Të tillët janë të zotët e anës së djathtë.';
$TRANSLATE['90']['19'] = 'Ndërsa ata që nuk i besuan argumentet Tona, ata jaanë të të majtës.';
$TRANSLATE['90']['20'] = 'Kundër tyre është zjarri i mbyllur.';
$TRANSLATE['91']['1'] = 'Pasha diellin dhe dritën e tij!';
