$TRANSLATE['92']['15'] = 'Që aty nuk hyn tjëtërkush, pos atij që është më i prishuri.';
$TRANSLATE['92']['16'] = 'I cili përgënjeshtroi dhe u zmbraps.';
$TRANSLATE['92']['17'] = 'Ndërsa ai që është më i devotshëm do të jetë larg tij.';
$TRANSLATE['92']['18'] = 'Ai që e jep pasurinë e vet e pastrohet,';
$TRANSLATE['92']['19'] = 'E jo për ta shpërblyer ndokë që i ka bërë mirë më parë.';
$TRANSLATE['92']['20'] = 'Por vetëm për të fituar kënaqësinë e Zotit të vet, më të lartit,';
$TRANSLATE['92']['21'] = 'Për Zotin, ai do të gëzojë atë kënaqësi!';
$TRANSLATE['93']['1'] = 'Pasha paraditën!';
$TRANSLATE['93']['2'] = 'Pasha natën kur shtrinë errësirën!';
$TRANSLATE['93']['3'] = 'Zoti yt nuk të ka lënë, as nuk të ka përbuzur.';
$TRANSLATE['93']['4'] = 'Dhe se bota e ardhshme është shumë më e mirë për ty se e para.';
$TRANSLATE['93']['5'] = 'E Zoti yt do të jepë ty, e ti do të kënaqesh.';
$TRANSLATE['93']['6'] = 'A nuk të gjeti ty jetim, e Ai të bëri vend (të dha përkrahje).';
$TRANSLATE['93']['7'] = 'Dhe të gjeti të paudhëzuar e Ai të udhëzojë.';
$TRANSLATE['93']['8'] = 'dhe të gjeti të varfër, e Ai të begatoi.';
