$TRANSLATE['96']['4'] = 'Është Ai që ua mësoi pendën.';
$TRANSLATE['96']['5'] = 'Ia mësoi njeriut atë që nuk e dinte.';
$TRANSLATE['96']['6'] = 'Jo, është e vërtetë se ai i kalon kufintë,';
$TRANSLATE['96']['7'] = 'Për shkak se e ndien veten të pavarur.';
$TRANSLATE['96']['8'] = 'Po kthimi është vetëm te Zoti yt.';
$TRANSLATE['96']['9'] = 'A më tregon atë që pengon,';
