$TRANSLATE['97']['1'] = 'Ne e zbritëm atë (Kur’anin) në natën e Kadrit.';
$TRANSLATE['97']['2'] = 'E ç’të bëri ty të dijsh se ç’është nata e Kadrit?';
$TRANSLATE['97']['3'] = 'Nata e Kadrit është më e rëndësishme se një mijë muaj!';
$TRANSLATE['97']['4'] = 'Me lejen e Zotit të tyre në (atë natë) të zbresin engjëj dhe shpirti (Xhibrili) për secilën çështje.';
$TRANSLATE['97']['5'] = 'Ajo (që përcakton Zoti) është paqe deri në agim të mëngjesit.';
$TRANSLATE['98']['1'] = 'Ata nga ithtarët e librit dhe idhujtarët, të cilët nuk besuan, nuk iu ndanë (besimit të tyre) derisa atyre u erdhi argumenti i qartë,';
$TRANSLATE['98']['2'] = 'I dërguari prej All-llahut, që lexon fletë të pastra,';
$TRANSLATE['98']['3'] = 'Në të cilat ka vetëm dispozita të drejta.';
$TRANSLATE['98']['4'] = 'Dhe ata të cilëve u është dhënë libri, nuk u ndanë vetëm pasi që u erdhi argumenti.';
$TRANSLATE['98']['5'] = 'E duke qenë se ata nuk ishin të urdhëruar me tjetër, pos që ta adhuronin All-llahun me një adhurim të sinqertë ndaj Tij, që të largohen prej çdo besimi të kotë, ta falin namazin, të japin zeqatin, se ajo është feja e drejtë.';
$TRANSLATE['98']['6'] = 'S’ka dyshim se ata, të cilët nuk besuan nga ithtarët e librit dhe nga idhujtarët, do të jenë në zjarrin e Xhehennemit, aty do të jenë përgjithmonë. Të tilët janë krijesa më e dëmshme.';
$TRANSLATE['98']['7'] = 'Ndërsa, ata që besuan dhe bënë vepra të mira, të tillët janë krijesa më e dobishme.';
$TRANSLATE['98']['8'] = 'Shpërblimi për ta te Zoti i tyre janë: vendet ne Xhennete nëpër të cilët burojnë lumenj, e aty janë përgjithmonë, pakëputur. All-llahu është i kënaqur ndaj tyre, e edhe ata të kënaqur prej Tij. E ky është për atë, që i ka pasur frikën Zotit të vet.';
$TRANSLATE['99']['1'] = 'Kur të dridhet toka me dridhjen e saj të fuqishme,';
$TRANSLATE['99']['2'] = 'Dhe të nxjerrë toka atë që ka në mbrendinë e saj (barrën nga brenda),';
$TRANSLATE['99']['3'] = 'Dhe njeriu të thotë: “ç’ka kjo (që bën këtë dridhje)?”';
$TRANSLATE['99']['4'] = 'Atë ditë, ajo i rrëfen tregimet e veta,';
$TRANSLATE['99']['5'] = 'Ngase Zoti yt e ka urdhëruar atë.';
$TRANSLATE['99']['6'] = 'Atë ditë njerëzit shfaqen të ndarë në grupe që të shpërblehen për veprat e tyre.';
$TRANSLATE['99']['7'] = 'E kushe punoi ndonjë të mirë, që pëshon sa grimca, atë do ta gjejë.';
$TRANSLATE['99']['8'] = 'Dhe kush punoi ndonjë të keqe sa grimca, atë do ta gjejë.';
$TRANSLATE['100']['1'] = 'Pasha vrapuesit e shpejtë (kuajt) që hingëllojnë!';
$TRANSLATE['100']['2'] = 'E që me thundrat e tyre nxjerrin xixëllima zjarri nga gurët,';
$TRANSLATE['100']['3'] = 'Dhe të cilët vërsulen herët në agim.';
$TRANSLATE['100']['4'] = 'E me të (vërsulje) atëherë çojnë pluhur.';
$TRANSLATE['100']['5'] = 'Dhe ashtu hidhen në mesin e grumbullit (të armikut).';
