$TRANSLATE['100']['7'] = 'Edhe vetë është dëshmues i asaj (përbuzje).';
$TRANSLATE['100']['8'] = 'Dhe ai është që shumë e do pasurinë.';
$TRANSLATE['100']['9'] = 'A nuk e di ai se kur të nxirret çka ëstë në varreza?';
$TRANSLATE['100']['10'] = 'Dhe të tubohet (të dalë në shesh) ajo që ishte në gjoksa.';
$TRANSLATE['100']['11'] = 'E atë ditë, pa dyshim Zoti i tyre është i njohur për punën e tyre.';
