$TRANSLATE['101']['3'] = 'Dhe ç’të mësoi ty se ç’është krisma?';
$TRANSLATE['101']['4'] = 'Ajo është ditë kur njerëzit bëhen si insekte të shpërndara.';
$TRANSLATE['101']['5'] = 'Dhe kodrat bëhen si leshi i shprishur.';
$TRANSLATE['101']['6'] = 'E sa i përket atij që i rëndohet peshoja e veprave të tij,';
$TRANSLATE['101']['7'] = 'Ai është në një jetë të këndshme.';
$TRANSLATE['101']['8'] = 'Ndërsa, atij që ka peshojën e lehtë të veprave të tij,';
$TRANSLATE['101']['9'] = 'Vendi i tij do të jetë në Havije.';
