$TRANSLATE['101']['11'] = 'Është zjarri me nxehtësi shumë të lartë.';
$TRANSLATE['102']['1'] = 'Juve u preokupoi përpjekja për shumimin (e pasurirë, të fëmijve, të pozitës)!';
$TRANSLATE['102']['2'] = 'Derisa të mos vizitoni varrezat (të bëheni banues të tyre - të vdisni).';
$TRANSLATE['102']['3'] = 'Jo, nuk është ashtu! Gjithsesi këtë do ta kuptoni më vonë!';
$TRANSLATE['102']['4'] = 'Përsëri jo, jeni gabim! Më vonë do ta kuptoni!';
