$TRANSLATE['103']['1'] = 'Pasha kohën!';
$TRANSLATE['103']['2'] = 'Nuk ka dyshim njeriu është në një humbje të sigurt.';
$TRANSLATE['103']['3'] = 'Me përjashtim të atyre që besuan, që bënë vepra të mira, që porositën njëri-tjetrin t’i përmbahen të vërtetës dhe që këshilluan njëri-tjetrin të jenë të durueshëm.';
$TRANSLATE['104']['1'] = 'Mjerë për secilin që e ofendon e përqesh (njerëzit),';
$TRANSLATE['104']['2'] = 'Që ka tubuar pasuri dhe që atë e ruan të mos i pakësohet.';
$TRANSLATE['104']['3'] = 'E mendon se pasuria e tij do të bëjë të përjetshëm.';
