$TRANSLATE['75']['8'] = 'Ay tutulur,';
$TRANSLATE['75']['9'] = 'Günes ve ay toplanir,';
$TRANSLATE['75']['10'] = 'Iste o gün insan, "kaçacak yer neresi?" der.';
$TRANSLATE['75']['11'] = 'Hayir, hayir, yok bir siper.';
$TRANSLATE['75']['12'] = 'O gün varilip durulacak yer, ancak Rabbinin huzurudur.';
$TRANSLATE['75']['13'] = 'O gün insana, yapip öne sürdügü ve geri biraktigi ne varsa bildirilir.';
$TRANSLATE['75']['14'] = 'Dogrusu insan kendi nefsini görür,';
$TRANSLATE['75']['15'] = 'Bir takim özürler ortaya atsa da.';
