$TRANSLATE['75']['17'] = 'Kuskusuz onu toplamak ve okumak bize aittir.';
$TRANSLATE['75']['18'] = 'O halde biz onu okudugumuz zaman sen onun okunusunu takip et.';
$TRANSLATE['75']['19'] = 'Sonra onu açiklamak da bize aittir.';
