$TRANSLATE['75']['23'] = 'Rabbine bakar.';
$TRANSLATE['75']['24'] = 'Yüzler de var ki o gün asiktir.';
$TRANSLATE['75']['25'] = 'Anlar ki kendisine belkiran (bel kemiklerini kiran belali bir is) yapilir.';
$TRANSLATE['75']['26'] = 'Hayir hayir, ne zaman ki can köprücük kemiklerine dayanir,';
$TRANSLATE['75']['27'] = '"Tedavi edebilecek kimdir?" denilir.';
$TRANSLATE['75']['28'] = 'Can çekisen bunun o ayrilik ani oldugunu anlar.';
