$TRANSLATE['79']['8'] = 'Yürekler vardir, o gün kaygidan hoplar.';
$TRANSLATE['79']['9'] = 'Gözler kalkmaz saygidan.';
$TRANSLATE['79']['10'] = 'Diyorlar ki: "Biz tekrar eski halimize mi döndürülecekmisiz?';
$TRANSLATE['79']['11'] = '"Biz, çürümüs kemikler olduktan sonra ha?"';
$TRANSLATE['79']['12'] = '"Öyleyse bu çok zararli bir dönüstür." dediler.';
$TRANSLATE['79']['13'] = 'Fakat o bir tek haykiristir.';
$TRANSLATE['79']['14'] = 'Bir de bakarsin hepsi meydandadir.';
$TRANSLATE['79']['15'] = 'Musa\'nin haberi sana geldi mi?';
$TRANSLATE['79']['16'] = 'Hani Rabbi ona kutsal vaadi Tuva\'da seslenmisti:';
$TRANSLATE['79']['17'] = '"Haydi, demisti, git Firavun\'a, çünkü o çok azdi."';
$TRANSLATE['79']['18'] = 'De ki: Ister misin arinasin?';
$TRANSLATE['79']['19'] = 'Seni Rabbinin yoluna ileteyim de ondan korkasin.';
$TRANSLATE['79']['20'] = 'Musa Firavun\'a o büyük mucizeyi gösterdi.';
$TRANSLATE['79']['21'] = 'Fakat Firavun yalanladi, karsi geldi.';
$TRANSLATE['79']['22'] = 'Sonra kosarak dönüp gitti.';
$TRANSLATE['79']['23'] = 'Derken adamlarini topladi da bagirdi:';
$TRANSLATE['79']['24'] = '"Ben sizin en yüce Rabbinizim" dedi.';
$TRANSLATE['79']['25'] = 'Allah da onu tuttu, dünya ve ahiret azabiyla yakalayiverdi.';
$TRANSLATE['79']['26'] = 'Kuskusuz bunda, saygi duyacaklar için bir ibret vardir.';
$TRANSLATE['79']['27'] = 'Yaratilisça siz mi daha çetinsiniz, yoksa gök mü? Onu Allah bina etti.';
$TRANSLATE['79']['28'] = 'Tavanini yükseltti, onu bir düzene koydu.';
$TRANSLATE['79']['29'] = 'Gecesini karartti, kuslugunu çikardi.';
$TRANSLATE['79']['30'] = 'Bundan sonra da yeryüzünü dösedi.';
$TRANSLATE['79']['31'] = 'Ondan suyunu ve otlagini çikardi.';
$TRANSLATE['79']['32'] = 'Daglarini oturttu.';
$TRANSLATE['79']['33'] = 'Sizin ve hayvanlarinizin geçimi için .';
$TRANSLATE['79']['34'] = 'Fakat o her seyi bastiran büyük felaket geldigi vakit,';
$TRANSLATE['79']['35'] = 'O, insanin neyin pesinde kostugunu anladigi gün,';
$TRANSLATE['79']['36'] = 'Gören kimseler için cehennem hortlatildigi vakit,';
$TRANSLATE['79']['37'] = 'Artik her kim azginlik etmis,';
$TRANSLATE['79']['38'] = 'Ve dünya hayatini tercih etmisse,';
$TRANSLATE['79']['39'] = 'Kuskusuz onun varacagi yer cehennemdir.';
$TRANSLATE['79']['40'] = 'Kim de Rabbinin divaninda durmaktan korkmus, nefsini bos heveslerden menetmis ise,';
$TRANSLATE['79']['41'] = 'Kuskusuz onun varacagi yer cennettir.';
$TRANSLATE['79']['42'] = 'Sana o kiyameti soruyorlar, ne zaman kopacak diye.';
$TRANSLATE['79']['43'] = 'Sen nerde, onu anlatmak nerde?!';
$TRANSLATE['79']['44'] = 'Onun son ilmi Rabbine aittir.';
$TRANSLATE['79']['45'] = 'Sen ancak ondan korkacak olanlari uyaricisin.';
$TRANSLATE['79']['46'] = 'Onlar o kiyameti görecekleri gün sanki dünyada bir aksam veya kuslugundan baska durmamisa dönecekler.';
$TRANSLATE['80']['1'] = '(Peygamber) Yüzünü eksitti ve döndü.';
$TRANSLATE['80']['2'] = 'Kendisine âmâ geldi, diye.';
$TRANSLATE['80']['3'] = 'Ne bilirsin, belki o temizlenecek?';
$TRANSLATE['80']['4'] = 'Veya ögüt belleyecek de ögüt ona fayda verecek.';
$TRANSLATE['80']['5'] = 'Ama buna ihtiyaç hissetmeyene gelince,';
$TRANSLATE['80']['6'] = 'Sen ona yöneliyorsun.';
$TRANSLATE['80']['7'] = 'Onun temizlenmemesinden sana ne?';
$TRANSLATE['80']['8'] = 'Ama sana can atarak gelen,';
$TRANSLATE['80']['9'] = 'Allah\'tan korkarak gelmisken,';
$TRANSLATE['80']['10'] = 'Sen onunla ilgilenmiyorsun.';
$TRANSLATE['80']['11'] = 'Hayir hayir, sakin. Çünkü o Kur\'ân bir ögüttür.';
$TRANSLATE['80']['12'] = 'Artik dileyen onu düsünür.';
$TRANSLATE['80']['13'] = 'O, degerli sahifelerdedir.';
$TRANSLATE['80']['14'] = 'Yüksek tutulan tertemiz sahifelerde.';
$TRANSLATE['80']['15'] = 'Yazicilarin ellerindedir,';
$TRANSLATE['80']['16'] = 'Degerli, iyi yazicilarin.';
$TRANSLATE['80']['17'] = 'O kahrolasi insan, ne nankör sey.';
$TRANSLATE['80']['18'] = 'O yaratan onu hangi seyden yaratti?';
$TRANSLATE['80']['19'] = 'Bir damla sudan, onu yaratti da biçime koydu.';
$TRANSLATE['80']['20'] = 'Sonra ona yolunu kolaylastirdi.';
$TRANSLATE['80']['21'] = 'Sonra onu öldürdü de kabre koydurdu.';
$TRANSLATE['80']['22'] = 'Sonra diledigi vakit onu tekrar diriltir.';
$TRANSLATE['80']['23'] = 'Hayir hayir, dogrusu o, hiç Allah\'in emrini tam yerine getirmedi,';
$TRANSLATE['80']['24'] = 'Bir de o insan yiyecegine baksin.';
$TRANSLATE['80']['25'] = 'Biz o suyu bol bol döktük.';
$TRANSLATE['80']['26'] = 'Sonra topragi nasil da yardik.';
$TRANSLATE['80']['27'] = 'Bu suretle orada ekinler bitirdik.';
$TRANSLATE['80']['28'] = 'Üzümler, yoncalar,';
$TRANSLATE['80']['29'] = 'Zeytinlikler, hurmaliklar,';
$TRANSLATE['80']['30'] = 'Iri ve sik agaçli bahçeler,';
$TRANSLATE['80']['31'] = 'Meyveler, çayirlar bitirdik.';
$TRANSLATE['80']['32'] = 'Siz ve hayvanlariniz faydalansin diye.';
$TRANSLATE['80']['33'] = 'Kulaklari sagir eden o gürültü geldiginde,';
$TRANSLATE['80']['34'] = 'O gün kisi kaçar, kardesinden...';
$TRANSLATE['80']['35'] = 'Anasindan , babasindan..';
$TRANSLATE['80']['36'] = 'Esinden ve ogullarindan.';
$TRANSLATE['80']['37'] = 'Onlardan her birinin o gün basindan asan isi vardir.';
$TRANSLATE['80']['38'] = 'Yüzler var ki, o gün paril paril,';
$TRANSLATE['80']['39'] = 'Güler, sevinir.';
$TRANSLATE['80']['40'] = 'Yüzler de var ki, o gün tozlanmis,';
$TRANSLATE['80']['41'] = 'Onlari karanlik bürümüs,';
$TRANSLATE['80']['42'] = 'Iste onlardir kâfirler, haktan sapanlar.';
$TRANSLATE['81']['1'] = 'Günes katlanip dürüldügünde,';
$TRANSLATE['81']['2'] = 'Yildizlar bulandiginda,';
$TRANSLATE['81']['3'] = 'Daglar yürütüldügünde,';
$TRANSLATE['81']['4'] = 'Kiyilmaz mallar birakildiginda,';
$TRANSLATE['81']['5'] = 'Vahsi hayvanlar bir araya toplandiginda,';
$TRANSLATE['81']['6'] = 'Denizler ateslendiginde (sulari çekilip, volkanlar halinde ates püskürdügünde),';
$TRANSLATE['81']['7'] = 'Nefisler eslestirildiginde (iyiler iyilerle, kötüler kötülerle bir araya toplandiginda),';
$TRANSLATE['81']['8'] = 'Diri diri topraga gömülen kiza soruldugunda,';
$TRANSLATE['81']['9'] = '"Hangi günahtan dolayi öldürüldü?" diye.';
$TRANSLATE['81']['10'] = 'Amel defterleri açildiginda,';
$TRANSLATE['81']['11'] = 'Gök siyrilip açildiginda,';
$TRANSLATE['81']['12'] = 'Cehennem kizistirildiginda,';
$TRANSLATE['81']['13'] = 'Ve cennet yaklastirildiginda,';
$TRANSLATE['81']['14'] = 'Herkes ne getirmis oldugunu anlar.';
$TRANSLATE['81']['15'] = 'Simdi yemin ederim o sinenlere (gündüzleri gözden kaybolan yildizlara),';
$TRANSLATE['81']['16'] = 'O akip akip yuvasina gidenlere,';
$TRANSLATE['81']['17'] = 'Yöneldigi an geceye,';
$TRANSLATE['81']['18'] = 'Nefeslendigi (agardigi) an sabaha ki,';
$TRANSLATE['81']['19'] = 'Kuskusuz o Kur\'an, degerli bir elçinin sözüdür.';
$TRANSLATE['81']['20'] = 'O elçi güçlüdür, Ars\'in sahibinin yaninda çok itibarlidir.';
$TRANSLATE['81']['21'] = 'Orada ona itaat edilir, güvenilir.';
$TRANSLATE['81']['22'] = 'Arkadasinizi cin çarpmis degildir.';
$TRANSLATE['81']['23'] = 'Andolsun o, Cebrail\'i açik ufukta gördü.';
$TRANSLATE['81']['24'] = 'O, gayb hakkinda cimri de degildir.';
$TRANSLATE['81']['25'] = 'O, kovulmus bir seytanin sözü degildir.';
$TRANSLATE['81']['26'] = 'Hâl böyle iken, siz nereye gidiyorsunuz?';
$TRANSLATE['81']['27'] = 'O, âlemler için ögütten baska bir sey degildir,';
$TRANSLATE['81']['28'] = 'Içinizden dogru gitmek isteyenler için.';
$TRANSLATE['81']['29'] = 'Âlemlerin Rabbi olan Allah dilemeyince, siz dileyemezsiniz.';
$TRANSLATE['82']['1'] = 'Gök çatladigi vakit,';
$TRANSLATE['82']['2'] = 'Yildizlar döküldügü vakit,';
$TRANSLATE['82']['3'] = 'Denizler yarilip akitildigi vakit,';
$TRANSLATE['82']['4'] = 'Kabirlerin içi disina getirildigi vakit,';
$TRANSLATE['82']['5'] = 'Herkes neyi önünden gönderdigini ve neyi geri biraktigini bilir.';
$TRANSLATE['82']['6'] = 'Ey insan! Ihsani bol Rabb\'ine karsi seni aldatan nedir?';
$TRANSLATE['82']['7'] = 'O Allah ki seni yaratti, seni düzgün yapili kilip ölçülü bir biçim verdi.';
$TRANSLATE['82']['8'] = 'Seni diledigi her hangi bir sekilde parçalardan olusturdu.';
$TRANSLATE['82']['9'] = 'Hayir hayir, siz cezayi yalanliyorsunuz.';
$TRANSLATE['82']['10'] = 'Oysa üzerinizde koruyucular var.';
$TRANSLATE['82']['11'] = 'Degerli yazicilar';
$TRANSLATE['82']['12'] = 'Onlar, siz her ne yaparsaniz bilirler';
$TRANSLATE['82']['13'] = 'Kuskusuz iyiler nimet içindedirler.';
$TRANSLATE['82']['14'] = 'Kötüler de cehennemdedirler.';
$TRANSLATE['82']['15'] = 'Ceza günü ona girecekler.';
$TRANSLATE['82']['16'] = 'Onlar o cehennemin gözünden kaçamazlar.';
$TRANSLATE['82']['17'] = 'Ceza gününün ne oldugunu sen bilir misin?';
$TRANSLATE['82']['18'] = 'Evet, bilir misin nedir acaba o ceza günü?';
$TRANSLATE['82']['19'] = 'O gün, hiç kimsenin baskasi için hiçbir seye sahip olamadigi gündür. O gün buyruk yalniz Allah\'indir.';
$TRANSLATE['83']['1'] = 'Eksik ölçüp tartanlarin vay haline!';
$TRANSLATE['83']['2'] = 'Onlar insanlardan kendilerine bir sey aldiklari zaman tam ölçerler.';
$TRANSLATE['83']['3'] = 'Kendileri baskalarina bir sey ölçtükleri veya tarttiklari zaman eksik ölçer ve tartarlar.';
$TRANSLATE['83']['4'] = 'Onlar tekrar diriltileceklerini zannetmiyorlar mi?';
$TRANSLATE['83']['5'] = 'Büyük bir gün için.';
$TRANSLATE['83']['6'] = 'Öyle bir gün ki, insanlar o gün Rabblerinin huzurunda divan duracaklar.';
$TRANSLATE['83']['7'] = 'Hayir hayir, kötülerin yazisi muhakkak Siccin\'dedir.';
$TRANSLATE['83']['8'] = 'Bildin mi sen, Siccin nedir?';
$TRANSLATE['83']['9'] = 'Yazilmis bir kitaptir o.';
$TRANSLATE['83']['10'] = 'Vay haline yalanlayanlarin o gün!';
$TRANSLATE['83']['11'] = 'Onlar ceza gününü yalanlayanlardir.';
$TRANSLATE['83']['12'] = 'Onu ancak siniri asan ve günaha düskün olanlar yalanlar.';
$TRANSLATE['83']['13'] = 'Ona âyetlerimiz okundugu zaman, "eskilerin masallari" der.';
$TRANSLATE['83']['14'] = 'Hayir hayir, öyle degil. Aksine onlarin kazandigi günahlar kalplerinin üzerine pas olmustur.';
$TRANSLATE['83']['15'] = 'Hayir hayir, dogrusu onlar o gün Rablerini görmekten mahrumdurlar.';
$TRANSLATE['83']['16'] = 'Sonra onlar muhakkak cehenneme girecekler.';
$TRANSLATE['83']['17'] = 'Sonra da onlara: "Iste bu, yalanlayip durdugunuz seydir" denilecek.';
$TRANSLATE['83']['18'] = 'Hayir hayir, iyilerin yazisi muhakkak Illiyyîn\'dedir.';
$TRANSLATE['83']['19'] = 'Bildin mi sen, Illiyyîn nedir?';
$TRANSLATE['83']['20'] = 'Yazilmis bir kitaptir o.';
$TRANSLATE['83']['21'] = 'Allah\'a yaklastirilmis melekler ona tanik olurlar.';
$TRANSLATE['83']['22'] = 'Haberiniz olsun ki, iyiler nimet içindedir.';
$TRANSLATE['83']['23'] = 'Tahtlar üzerinde etrafa bakarlar.';
$TRANSLATE['83']['24'] = 'Yüzlerinde nimet ve mutlulugun sevincini görürsün.';
$TRANSLATE['83']['25'] = 'Onlara damgali saf bir içki sunulur.';
$TRANSLATE['83']['26'] = 'Onun sonu misktir. Iste ona imrensin artik imrenenler.';
$TRANSLATE['83']['27'] = 'Karisimi Tesnim\'dendir (En üstün cennet sarabindandir).';
$TRANSLATE['83']['28'] = 'Allah\'a yakin olanlarin içecekleri bir kaynaktir o.';
$TRANSLATE['83']['29'] = 'Dogrusu o suç isleyenler inananlara gülüyorlardi.';
$TRANSLATE['83']['30'] = 'Onlara ugradiklari vakit birbirlerine göz kirpiyorlardi.';
$TRANSLATE['83']['31'] = 'Evlerine döndükleri zaman zevklenerek dönüyorlardi.';
$TRANSLATE['83']['32'] = 'Müminleri gördükleri vakit; "iste bunlar sapiklar" diyorlardi.';
$TRANSLATE['83']['33'] = 'Oysa onlar müminler üzerine bekçi olarak gönderilmemislerdi.';
$TRANSLATE['83']['34'] = 'Iste bugün de inananlar kâfirlere gülecek.';
$TRANSLATE['83']['35'] = 'Koltuklar üzerinde etrafa bakacaklar.';
$TRANSLATE['83']['36'] = 'Nasil, kâfirler yaptiklarinin cezasini buldular mi?';
$TRANSLATE['84']['1'] = 'Gök yarildigi,';
$TRANSLATE['84']['2'] = 'Rabbini dinleyip kendisine yarasir sekilde boyun egdigi vakit,';
$TRANSLATE['84']['3'] = 'Yer uzatilip düzlendigi,';
$TRANSLATE['84']['4'] = 'Içinde ne varsa attigi ve tamamen bosaldigi';
$TRANSLATE['84']['5'] = 'Ve Rabbini dinleyip kendisine yarasir sekilde boyun egdigi vakit,';
$TRANSLATE['84']['6'] = 'Ey insan! Kuskusuz sen Rabbine dogru çaba üstüne çaba sarfetmektesin, nihayet O\'na varacaksin.';
$TRANSLATE['84']['7'] = 'O vakit kitabi sag eline verilen,';
$TRANSLATE['84']['8'] = 'Kolay bir hesapla hesaba çekilecek,';
$TRANSLATE['84']['9'] = 'Ve sevinçli olarak ailesine dönecektir.';
$TRANSLATE['84']['10'] = 'Ama kitabi arkasindan verilen,';
$TRANSLATE['84']['11'] = '"Yetis ey ölüm!" diye bagiracak';
$TRANSLATE['84']['12'] = 'Ve alevli atese girecektir.';
$TRANSLATE['84']['13'] = 'Çünkü o ailesi içinde sevinçli idi.';
$TRANSLATE['84']['14'] = 'Hiç Rabbine dönmeyecegini sanmisti.';
$TRANSLATE['84']['15'] = 'Hayir Rabbi onu görmekte idi.';
$TRANSLATE['84']['16'] = 'Simdi, yemin ederim o safaga,';
$TRANSLATE['84']['17'] = 'Geceye ve içinde barindirdigi seylere,';
$TRANSLATE['84']['18'] = 'Derlendigi zaman o aya,';
$TRANSLATE['84']['19'] = 'Ki, siz elbette halden hale geçeceksiniz.';
$TRANSLATE['84']['20'] = 'Böyleyken onlar neden acaba iman etmezler?';
$TRANSLATE['84']['21'] = 'Karsilarinda Kur\'ân okundugu vakit secde etmezler?';
$TRANSLATE['84']['22'] = 'Aksine o nankörler yalanliyorlar.';
$TRANSLATE['84']['23'] = 'Oysa Allah içlerinde sakladiklarini biliyor.';
$TRANSLATE['84']['24'] = 'Onun için onlara elem verici bir azabi müjdele.';
$TRANSLATE['84']['25'] = 'Ancak iman edip iyi ameller isleyenler baskadir. Onlara tükenmez bir ecir vardir.';
$TRANSLATE['85']['1'] = 'Burçlar sahibi gökyüzüne,';
$TRANSLATE['85']['2'] = 'Vaad olunan o güne,';
$TRANSLATE['85']['3'] = 'Sahitlik edene ve edilene andolsun ki,';
$TRANSLATE['85']['4'] = 'Kahroldu o hendegin sahipleri,';
$TRANSLATE['85']['5'] = 'O çirali atesin,';
$TRANSLATE['85']['6'] = 'Hani o atesin basina oturmuslar,';
$TRANSLATE['85']['7'] = 'Müminlere yaptiklarini seyrediyorlardi.';
$TRANSLATE['85']['8'] = 'Müminlere kizmalarinin sebebi de, onlarin yalniz çok güçlü ve övgüye lâyik olan Allah\'a iman etmeleri idi.';
$TRANSLATE['85']['9'] = 'O Allah ki, göklerin ve yerin hükümranligi O\'nundur ve Allah her seye sahittir.';
$TRANSLATE['85']['10'] = 'Inanan erkek ve kadinlara iskence yapip sonra da tevbe etmeyenlere cehennem azabi ve yangin azabi vardir.';
$TRANSLATE['85']['11'] = 'Inanan ve iyi amel yapanlar için de altindan irmaklar akan cennetler vardir. Iste büyük kurtulus odur.';
$TRANSLATE['85']['12'] = 'Kuskusuz Rabbinin yakalamasi serttir.';
$TRANSLATE['85']['13'] = 'Yoktan o yaratir ve tekrar o diriltir.';
$TRANSLATE['85']['14'] = 'Bununla beraber çok bagislayandir, çok sevendir.';
$TRANSLATE['85']['15'] = 'Ars\'in sahibidir, yücedir.';
$TRANSLATE['85']['16'] = 'Diledigini yapandir.';
$TRANSLATE['85']['17'] = 'O ordularin kissasi sana geldi mi?';
$TRANSLATE['85']['18'] = 'Yani Firavun ve Semud\'un?';
$TRANSLATE['85']['19'] = 'Fakat o inkarcilar hâlâ bir yalanlama içinde.';
$TRANSLATE['85']['20'] = 'Oysa Allah onlari arkalarindan kusatmistir.';
$TRANSLATE['85']['21'] = 'Hayir o serefli bir Kur\'ân\'dir.';
$TRANSLATE['85']['22'] = 'Levh-i Mahfuz\'dadir.';
$TRANSLATE['86']['1'] = '1- Andolsun o göge ve Târik\'a,';
$TRANSLATE['86']['2'] = 'Târik nedir, bildin mi?';
$TRANSLATE['86']['3'] = 'O, karanligi delen yildizdir.';
$TRANSLATE['86']['4'] = 'Hiçbir nefis yoktur ki basinda bir denetleyici bulunmasin.';
$TRANSLATE['86']['5'] = 'Onun için insan neden yaratildigina bir baksin.';
$TRANSLATE['86']['6'] = 'Atilan bir sudan yaratildi.';
$TRANSLATE['86']['7'] = 'O su, erkegin sulbü ile kadinin gögüs kemikleri arasindan çikar.';
$TRANSLATE['86']['8'] = 'Elbette Allah\'in onu döndürmeye gücü yeter.';
$TRANSLATE['86']['9'] = 'O gün bütün sirlar yoklanip, meydana çikarilir.';
$TRANSLATE['86']['10'] = 'Insanin o gün ne bir gücü vardir, ne de bir yardimcisi.';
$TRANSLATE['86']['11'] = 'Andolsun o dönüslü göge,';
$TRANSLATE['86']['12'] = 'O yarilip çatlayan yere,';
$TRANSLATE['86']['13'] = 'Kuskusuz Kur\'ân, ayirici bir sözdür.';
$TRANSLATE['86']['14'] = 'O asla bir saka degildir.';
$TRANSLATE['86']['15'] = 'Haberin olsun ki, kâfirler hep hile kuruyorlar.';
$TRANSLATE['86']['16'] = 'Ben de hilelerine karsilik veririm.';
$TRANSLATE['86']['17'] = 'Onun için sen kâfirlere mühlet ver, onlara az bir zaman tani.';
$TRANSLATE['87']['1'] = 'Rabbinin yüce adini tesbih et.';
$TRANSLATE['87']['2'] = 'Yaratip düzene koyan O\'dur.';
$TRANSLATE['87']['3'] = 'Takdir edip hidayeti gösteren O\'dur.';
$TRANSLATE['87']['4'] = 'Otlagi çikaran,';
$TRANSLATE['87']['5'] = 'Sonra da onu karamsi bir sel köpügü haline getiren O\'dur.';
$TRANSLATE['87']['6'] = 'Bundan böyle sana Kur\'ân\'i okutacagiz da unutmayacaksin.';
$TRANSLATE['87']['7'] = 'Yalniz Allah\'in diledigi baskadir. Çünkü o açigi da bilir, gizliyi de.';
$TRANSLATE['87']['8'] = 'Seni en kolay yola muvaffak kilacagiz.';
$TRANSLATE['87']['9'] = 'Onun için ögüt ver, eger ögüt fayda verirse.';
$TRANSLATE['87']['10'] = 'Saygisi olan ögüt alacaktir.';
$TRANSLATE['87']['11'] = 'Pek bedbaht olan da ondan kaçinacaktir.';
$TRANSLATE['87']['12'] = 'O ki, en büyük atese girecektir.';
$TRANSLATE['87']['13'] = 'Sonra ne ölecek onda, ne de hayat bulacaktir.';
$TRANSLATE['87']['14'] = 'Doğrusu felah buldu (günahtan) temizlenen.';
$TRANSLATE['87']['15'] = 'Rabbinin adini anip namaz kilan.';
$TRANSLATE['87']['16'] = 'Fakat siz dünya hayatini tercih ediyorsunuz.';
$TRANSLATE['87']['17'] = 'Oysa ahiret daha hayirli ve daha kalicidir.';
$TRANSLATE['87']['18'] = 'Kuskusuz bu ilk sahifelerde vardir,';
$TRANSLATE['87']['19'] = 'Ibrahim ve Musa\'nin sahifelerinde.';
$TRANSLATE['88']['1'] = 'O her seyi kusatacak olan Kiyamet\'in haberi sana geldi mi?';
$TRANSLATE['88']['2'] = 'Yüzler var ki, o gün egilmis, zillete düsmüstür.';
$TRANSLATE['88']['3'] = 'Çalismis, yorulmustur.';
$TRANSLATE['88']['4'] = 'Kizismis bir atese girer.';
$TRANSLATE['88']['5'] = 'Onlara kizgin bir kaynaktan su verilir.';
$TRANSLATE['88']['6'] = 'Onlar için kuru bir dikenden baska yiyecek de yoktur.';
