$TRANSLATE['90']['10'] = 'Ona iki yolu gösterdik.';
$TRANSLATE['90']['11'] = 'Fakat o, o sarp yokusa gögüs veremedi.';
$TRANSLATE['90']['12'] = 'Bildin mi sen, o sarp yokus nedir?';
